INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES(207, 34, 'ekz08_19', 19, 'Juĝisto:  Ĉu vi do asertas, ke vi iris en la domon opiniante, ke ĝi estas via domo ?<br/>Akuzato: Jes, sinjoro juĝisto.<br/>Juĝisto: Sed kial vi forkuris, kiam la sinjorino envenis ?<br/>Akuzato: Mi opiniis, ke ŝi estas mia edzino !', 'Le juge: Donc, vous affirmez que vous êtes entré dans la maison en pensant qu\'elle était votre maison.<br/>Accusé : Oui, Monsieur le juge.<br/>Le juge: Mais pourquoi vous êtes-vous enfui quand la femme est entrée ?<br/>Accusé: J\'ai pensé qu\'elle était ma femme !', 'le juge donc vous affirmez que vous êtes entré dans la maison en pensant qu elle était votre maison accusé oui monsieur le juge le juge mais pourquoi vous êtes-vous enfui quand la femme est entrée accusé jai pensé quelle était ma femme', '', 0, 0);
