INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES(419, 72, 'ekz10_07', 7, 'Tom [*] Linda estas student[*]. En universitat[*] restoraci[*] ili observas blond[*] junul[*], kiu agas plej strange: dum li parolas [*] knabino, li kaŝ[*] metas io[*] en ŝi[*] kafotaso[*].', 'Tom <b>kaj</b> Linda estas student<b>oj</b>. En universitat<b>a</b> restoraci<b>o</b> ili observas blond<b>an</b> junul<b>on</b>, kiu agas plej strange: dum li parolas <b>al</b> knabino, li kaŝ<b>e</b> metas io<b>n</b> en ŝi<b>an</b> kafotaso<b>n</b>.', 'tom kaj linda estas studentoj en universitata restoracio ili observas blondan junulon kiu agas plej strange dum li parolas al knabino li kasxe metas ion en sxian kafotason', '', 0, 0);
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES(420, 72, 'ekz10_08', 8, 'Eble estis drogo, ĉar tuj post ki[*] ŝi ekstaras kaj forlasas la restoracio[*], [*] la koridoro[*] ŝi jam perdas la konscion. Intertemp[*], Bob sid[*]is ĉe Tom kaj Linda.', 'Eble estis drogo, ĉar tuj post ki<b>am</b> ŝi ekstaras kaj forlasas la restoracio<b>n</b>, en la koridor<b>o</b> ŝi jam perdas la konscion. Intertemp<b>e</b>, Bob sid<b>iĝ</b>is ĉe Tom kaj Linda.', 'eble estis drogo cxar tuj post kiam sxi ekstaras kaj forlasas la restoracion en la koridoro sxi jam perdas la konscion intertempe bob sidigxis cxe tom kaj linda', '', 0, 0);
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES(421, 72, 'ekz10_09', 9, 'Li povas rakont[*] al ili[*], ki[*] estas la knabino: ŝi estas Gerda, nova instruist[*] pri kriptaĵoscienco ([*]novaj sekretaj lingvoj). Ronga, la profesoro pri lingvistiko, volis, ke ŝi ven[*] al la universitato [*] instru[*] kaj labor[*] kun li.', 'Li povas rakont<b>i</b> al <b>ili</b>, kiu estas la knabino: ŝi estas Gerda, nova instruist<b>ino</b> pri kriptaĵoscienco (<b>mal</b>novaj sekretaj lingvoj). Ronga, la profesoro pri lingvistiko, volis, ke ŝi ven<b>u</b> al la universitato por instrui kaj labori kun li.', 'li povas rakonti al ili kiu estas la knabino sxi estas gerda nova instruistino pri kriptajxoscienco malnovaj sekretaj lingvoj ronga la profesoro pri lingvistiko volis ke sxi venu al la universitato por instrui kaj labori kun li', '', 0, 0);
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES(422, 72, 'ekz10_10', 10, 'Kiam ili aŭdas ŝi[*] falbruon el la koridor[*], Bob kaj Tom rapidas por helpi ŝi[*]. Bob metas ŝin bone, tiel [*] ŝi kuŝu komforte, kaj Tom ir[*] vok[*] flegistinon.', 'Kiam ili aŭdas ŝi<b>an</b> falbruon el la koridor<b>o</b>, Bob kaj Tom rapidas por helpi ŝi<b>n</b>. Bob metas ŝin bone, tiel <b>ke</b> ŝi kuŝu komforte, kaj Tom iras voki flegistinon.', 'kiam ili auxdas sxian falbruon el la koridoro bob kaj tom rapidas por helpi sxin bob metas sxin bone tiel ke sxi kusxu komforte kaj tom iras voki flegistinon', '', 0, 0);
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES(423, 72, 'ekz10_11', 11, 'Tiu flegistino, dika, grasa kaj parolema, nur post long[*] diskut[*] kun Tom, konsciiĝas [*] tio, ke la afero urĝas. Se ŝi est[*] pli zorgema, ŝi [*] rapidus kaj eble ne okazus jeno:', 'Tiu flegistino, dika, grasa kaj parolema, nur post long<b>a</b> diskut<b>ado</b> kun Tom, konsciiĝas <b>pri</b> tio, ke la afero urĝas. Se ŝi est<b>us</b> pli zorgema, ŝi <b>pli</b> rapidus kaj eble ne okazus jeno:', 'tiu flegistino dika grasa kaj parolema nur post longa diskutado|diskuto kun tom konsciigxas pri tio ke la afero urgxas se sxi estus pli zorgema sxi pli rapidus kaj eble ne okazus jeno', '', 0, 0);
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES(424, 72, 'ekz10_12', 12, 'Kiam Tom fine [*]venas kun la flegistino, Gerda estas for, kaj Bob sidas ĉe la koridor[*] mur[*], bat[*]a surkape. Kion [*]tempe faras Linda? Ŝi pli kaj pli [*]trankvil[*]as, lasite sola [*] la universitata restoracio kun la blond[*] strang[*].', 'Kiam Tom fine <b>al</b>venas kun la flegistino, Gerda estas for, kaj Bob sidas ĉe la koridor<b>a</b> mur<b>o</b>, bat<b>ita</b> surkape. Kion <b>dum</b>tempe faras Linda? Ŝi pli kaj pli <b>mal</b>trankvil<b>iĝ</b>as, lasite sola <b>en</b> la universitata restoracio kun la blond<b>a</b> strang<b>ulo</b>.', 'kiam Tom fine alvenas kun la flegistino gerda estas for kaj bob sidas cxe la koridora muro batita surkape kion dumtempe faras linda sxi pli kaj pli maltrankviligxas lasite sola en la universitata restoracio kun la blonda strangulo', '', 0, 0);
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES(425, 72, 'ekz10_13', 13, 'Ŝi observ[*] lin, kaj kiam li eliras tra la pordo alurb[*], ŝi[*] sekvas li[*]. Linda estas junulino iom tim[*], certe belega. Tom am[*]as ŝin; li havas okulojn nur [*] ŝi.', 'Ŝi observ<b>adas</b> lin, kaj kiam li eliras tra la pordo alurb<b>en</b>, <b>ŝi</b> sekvas li<b>n</b>. Linda estas junulino iom tim<b>ema</b>, certe belega. Tom am<b>eg</b>as ŝin; li havas okulojn nur <b>por</b> ŝi.', 'sxi observadas lin kaj kiam li eliras tra la pordo alurben sxi sekvas lin linda estas junulino iom timema certe belega tom amegas sxin li havas okulojn nur por sxi|sxi observas lin, kaj kiam li eliras tra la pordo alurben sxi sekvas lin linda estas junulino iom timema certe belega tom amegas sxin li havas okulojn nur por sxi', '', 0, 0);
