(1317, 153, NULL, 10, 'Donu instrukciojn por bona prizorgado de brakhorloĝo.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1318, 154, NULL, 11, 'La enlitiĝonto <i>laŭkutime</i> streĉis sian horloĝon.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1319, 154, NULL, 12, 'La instruisto <i>malgrandanime</i> malpermesis la feston.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1320, 154, NULL, 13, 'La horloĝo difektiĝis <i>oleomanke</i>.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1321, 154, NULL, 14, 'Mi volas <i>sange</i> bati la amason vantan!', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1322, 154, NULL, 15, 'La fantomoj <i>lunlume</i> revivas en pejzaĝo antaŭlonge konata.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1323, 154, NULL, 16, 'Aprilo <i>pluvokise</i> baldaŭ trempos foliojn.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1324, 154, NULL, 17, 'Mateno frapis la nokton <i>pugnalnaze</i>, kaj ĝia sango ruĝigas la horizonton.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1325, 154, NULL, 18, '<i>Brutmense</i> la homoj lin ignoris.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1326, 154, NULL, 19, 'Mi ŝutas for mian <i>dentgrince</i> gardatan trezoron, la Vivon.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1327, 154, NULL, 20, 'Blekis la kanonoj, kaj <i>fratobuĉe</i> falis milionoj.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1328, 154, NULL, 21, 'Per amo li ribelas <i>vivjese</i> kontraŭ la monstro de l\'pereo.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1329, 154, NULL, 22, 'La sklavoj komencis revi <i>buĉe</i>, ke baldaŭ venos venĝ-okazo!', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1330, 155, NULL, 23, '<i>Kiam ni estis geknaboj</i>, ni vadis en la maro.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1331, 155, NULL, 24, 'La raketoj brodis la nokton <i>per oraj fadenoj</i>.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1332, 155, NULL, 25, 'La albatroso <i>sur larĝaj flugiloj</i> ŝvebis super la ondoj.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1333, 155, NULL, 26, 'Ŝi forviŝis larmon, kiu aperis <i>en angulo de ŝia okulo</i>.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1334, 155, NULL, 27, 'Ili rimarkis la larmojn, kiuj ardadis <i>sur miaj vangoj</i>.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1335, 155, NULL, 28, '<i>Tirante la ŝultrojn</i>, li indiferente foriris.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1336, 155, NULL, 29, 'La knaboj interpugnis <i>ĝis la sango ŝprucis</i>.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1337, 155, NULL, 30, 'Mirinda multkolora brulo fontis sur la ĉielo, <i>kvazaŭ kreado de steloj</i>.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1338, 155, NULL, 31, '<i>Plena de zorgoj</i> li klinis sin denove al la libro.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1339, 155, NULL, 32, 'Horloĝo povas difektiĝi <i>pro diversaj influoj</i>.', NULL, NULL, NULL, NULL, 0, 0, NULL),
