(1356, 158, NULL, 15, 'Ĉu Sita ĉiam rememoros sian unuan ĉielarkon? Kial?', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1357, 159, NULL, 16, '“Ŝtelistoj forportis el mia domo kelkajn valorajn juvelojn”, raportis malgaje la domposedanto.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1358, 159, NULL, 17, '“Ŝtelisto klopodas eniri mian domon! Venu tuj!” kriis la domposedanto al la policano.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1359, 159, NULL, 18, '“Se mia amanto ne skribos baldaŭ al mi”, plorĝemis la junulino, “mi kredos, ke li forlasis min por ĉiam!”.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1360, 159, NULL, 19, '“En la baldaŭa estonteco”, konjektas la sciencisto, “homoj elpaŝos sur la lunsurfacon”.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1361, 159, NULL, 20, '“Ĉu ĉielarkojn vi kolektas?” demandis mia inkvizitoro.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1362, 159, NULL, 21, '“Kion? ... Ĉu ĉielarkojn vi kolektas?” demandis mia inkvizitoro kun la okuloj larĝe malfermitaj.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1363, 159, NULL, 22, '“Sed kiel komenci? Al vi ja estis facile komenci, vi ja havis fraton!” ekparolis la knabino.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1364, 159, NULL, 23, '“Sed ne kredu”, mi daŭrigis post ioma paŭzo, “ke ĉiuj ĉielarkoj el mia kolekto estas altvaloraj. Estas en ĝi ankaŭ kelkaj, kiujn mi kolektis nur pro strangeco”.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1365, 160, NULL, 24, '“Jes, mi venos morgaŭ laŭ nia interkonsento”, diris la kliento.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1366, 160, NULL, 25, '“Jes, ni nepre iru al la bankedo!” diris mia manĝema frato.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1367, 160, NULL, 26, '“Tiel amuzan prezentadon mi neniam pli frue ĉeestis”, mi diris al la komikulo.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1368, 160, NULL, 27, '“Laŭ liaj piedsignoj la ŝtelisto altis preskaŭ du metrojn”, diris la detektivo.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1369, 160, NULL, 28, '“En ĉiu malbono”, diris mia fratino, “troviĝas iom da bono.”', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1370, 160, NULL, 29, '“Ĉu vi vere donacas al mi tiel valoran rubenringon?” diris lia edzino.', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1371, 160, NULL, 30, '“Memoru bone”, mi diris, “ke vi nepre ne forlasu la domon ĉi-vespere!”', NULL, NULL, NULL, NULL, 0, 0, NULL),
(1372, 160, NULL, 31, '“Vi tute ne intencis ĉeesti la kunvenon”, mi diris, “kaj tial vi ja neniun paroladon pretigis!”', NULL, NULL, NULL, NULL, 0, 0, NULL),
