INSERT INTO `tekstoj` (`id`, `ekdato`, `titolo`, `auxtoro`, `arthur_id`, `fonto`, `sono`, `leganto`, `nivelo`, `vortoj`, `kolekto`, `etikedoj`, `enhavo`, `aktiva`) VALUES
('1001noktoj01', '2025-09-28 20:35:30', '1001 noktoj ', 'Daniel Moirand', 62220, 'https://butiko-esperanto-france.org/romans-nouvelles-recits-traductions/1324-la-libro-de-la-mil-kaj-unu-noktoj-3-volumes.html', 'https://ikurso.esperanto-france.org/audio/1001noktoj01.mp3', 'Francis Soghomonian', 1852, 2513, NULL, 'beletro, aventuro, fabelo', '[{\"teksto\": \"UNUA VOJA\\u011cO DE SINDBAD LA MARISTO\", \"vortaro\": {\"de\": \"de\", \"la\": \"le, la, les\", \"maristo\": \"marin\", \"sindbad\": \"Sindbad\", \"unua\": \"premier, premi\\u00e8re\", \"voja\\u011do\": \"voyage\"}}, {\"teksto\": \"\", \"vortaro\": {}}, {\"teksto\": \"Mi heredis de mia familio grandegan hava\\u0135on, mi mal\\u015dparis ties pli grandan parton en la dibo\\u0109oj de mia juneco; sed mi rekonscii\\u011dis kaj pripensante, mi agnoskis, ke la ri\\u0109a\\u0135oj estas efemeraj, kaj ke oni balda\\u016d vidas ties finon, kiam oni tiom malbone zorgas pri ili, kiel mi agis. Mi plie opiniis, ke mi vane mal\\u015dparas en sencela vivo la tempon, kiu estas en la mondo la plej valora elemento. Mi ankora\\u016d opiniis, ke estas la lasta kaj la plej beda\\u016drinda el la mizeroj esti malri\\u0109a en sia maljuna a\\u011do. Mi rememoris tiujn parolojn de la granda Salomono, kiujn mi anta\\u016de a\\u016ddis de mia patro, ke estas malpli penige trovi\\u011di en la tombo ol en la malri\\u0109eco.\", \"vortaro\": {\"agis\": \"agir\", \"agnoskis\": \"admettre, reconnaitre\", \"ankora\\u016d\": \"encore\", \"anta\\u016de\": \"avant, auparavant \", \"a\\u011do\": \"\\u00e2ge\", \"a\\u016ddis\": \"entendre\", \"balda\\u016d\": \"bient\\u00f4t\", \"beda\\u016drinda\": \"regrettable, f\\u00e2cheux\", \"de\": \"de\", \"dibo\\u0109oj\": \"d\\u00e9bauche\", \"efemeraj\": \"\\u00e9ph\\u00e9m\\u00e8re, fugace, passager\", \"el\": \"hors de, de\", \"elemento\": \"\\u00e9l\\u00e9ment-cl\\u00e9, \\u00e9l\\u00e9ment, coefficient, terme\", \"en\": \"dans\", \"estas\": \"\\u00eatre\", \"esti\": \"\\u00eatre\", \"familio\": \"famille\", \"finon\": \"habiter au diable vauvert, le meilleur est pour la fin, extr\\u00e9mit\\u00e9, fin, ach\\u00e8vement, arr\\u00eat\", \"granda\": \"grand(e)\", \"grandan\": \"grand(e)\", \"grandegan\": \"immense, \\u00e9norme, tr\\u00e8s grand\", \"hava\\u0135on\": \"avoir, bien, fortune\", \"heredis\": \"coh\\u00e9ritier, h\\u00e9riter, recevoir en h\\u00e9ritage, tenir\", \"ili\": \"ils, elles, eux\", \"juneco\": \"jeunesse\", \"kaj\": \"et\", \"ke\": \"que (exemple: je sais que\\u2026, il dit que\\u2026)\", \"kiam\": \"quand\", \"kiel\": \"comment, comme\", \"kiu\": \"qui\", \"kiujn\": \"qui\", \"la\": \"le, la, les\", \"lasta\": \"dernier\", \"malbone\": \"mal\", \"maljuna\": \"vieux, vieille\", \"malpli\": \"moins\", \"malri\\u0109a\": \"pauvre\", \"malri\\u0109eco\": \"paup\\u00e9risme, pauvret\\u00e9\", \"mal\\u015dparas\": \"dilapider, gaspiller\", \"mal\\u015dparis\": \"dilapider, gaspiller\", \"mi\": \"je\", \"mia\": \"je\", \"mizeroj\": \"paup\\u00e9risme, mis\\u00e8re\", \"mondo\": \"monde\", \"ol\": \"que (dans une comparaison)\", \"oni\": \"on\", \"opiniis\": \"\\u00eatre d\'avis, penser, croire\", \"parolojn\": \"parole\", \"parton\": \"partie\", \"patro\": \"p\\u00e8re\", \"penige\": \"p\\u00e9niblement, f\\u00e2cheux\", \"plej\": \"le plus, la plus\", \"pli\": \"plus (en plus grande quantit\\u00e9)\", \"plie\": \"de plus\", \"pri\": \"\\u00e0 propos de\", \"pripensante\": \"r\\u00e9fl\\u00e9chir \\u00e0, consid\\u00e9rer, envisager\", \"rekonscii\\u011dis\": \"reprendre conscience\", \"rememoris\": \"se rappeler, se souvenir\", \"ri\\u0109a\\u0135oj\": \"bien, richesse\", \"salomono\": \"Salomon\", \"sed\": \"mais\", \"sencela\": \"sans but, vain\", \"sia\": \"se, s\', soi\", \"tempon\": \"temps\", \"ties\": \"de celui-l\\u00e0, \\u00e0 celui-l\\u00e0\", \"tiom\": \"tant (de), autant\", \"tiujn\": \"ce, cet(te)\", \"tombo\": \"tombe\", \"trovi\\u011di\": \"se trouver\", \"valora\": \"couteux, de valeur\", \"vane\": \"en vain\", \"vidas\": \"voir\", \"vivo\": \"vie\", \"zorgas\": \"se faire du souci ; s\'occuper de, prendre soin de\"}}, {\"teksto\": \"Malserenigita de \\u0109iuj tiuj pripensoj, mi kolektis la resta\\u0135ojn de mia hereda\\u0135o, mi a\\u016dkcie vendis en la bazaro \\u0109ion, kion mi havis en mia domo. Mi poste asocii\\u011dis kun kelkaj komercistoj, kiuj okupi\\u011dis pri mara komerco. Mi konsultis tiujn, kiuj \\u015dajnis al mi kapablaj provizi min per bonaj konsiloj. Fine mi decidis fruktigi la malmulton da mono, kiu restis al mi, kaj tuj kiam mi decidis tion, mi mallonge atendis anta\\u016d ol efektivigi \\u011din. Mi tiam iris al Basra, kie mi en\\u015dipi\\u011dis kun pluraj komercistoj sur \\u015dipo, kiun ni frajtis danke al nia kunigita mono.\", \"vortaro\": {\"al\": \"\\u00e0, vers\", \"anta\\u016d\": \"avant, en avant de, devant\", \"asocii\\u011dis\": \"s\'associer\", \"atendis\": \"attendre\", \"a\\u016dkcie\": \"aux ench\\u00e8res\", \"bazaro\": \"march\\u00e9, \\u00e9picerie, bazar\", \"bonaj\": \"bon,  bonne\", \"da\": \"de (apr\\u00e8s une expression de quantit\\u00e9)\", \"danke\": \"avec reconnaissance\", \"de\": \"de\", \"decidis\": \"d\\u00e9cider\", \"domo\": \"maison\", \"efektivigi\": \"irr\\u00e9alisable, effectuer, mat\\u00e9rialiser, ex\\u00e9cuter, r\\u00e9aliser\", \"en\": \"dans\", \"en\\u015dipi\\u011dis\": \"embarquer\", \"fine\": \"au final, finalement, enfin\", \"frajtis\": \"fr\\u00e9ter\", \"fruktigi\": \"rendre f\\u00e9cond, rendre fertile, fertiliser\", \"havis\": \"avoir\", \"hereda\\u0135o\": \"h\\u00e9ritage, h\\u00e9r\\u00e9dit\\u00e9, legs, succession\", \"iris\": \"aller\", \"kaj\": \"et\", \"kapablaj\": \"capable, apte, bon\", \"kelkaj\": \"quelque, un certain\", \"kiam\": \"quand\", \"kie\": \"o\\u00f9\", \"kion\": \"quoi, que\", \"kiu\": \"qui\", \"kiuj\": \"qui\", \"kiun\": \"qui\", \"kolektis\": \"rassembler\", \"komercistoj\": \"commer\\u00e7ant\", \"komerco\": \"en affaires, on ne fait pas de sentiments, commerce, n\\u00e9goce\", \"konsiloj\": \"conseil\", \"konsultis\": \"consulter\", \"kun\": \"avec\", \"kunigita\": \"rapprocher, r\\u00e9unir, unir\", \"la\": \"le, la, les\", \"mallonge\": \"bri\\u00e8vement\", \"malmulton\": \"peu de chose\", \"malserenigita\": \"frapp\\u00e9, abbatu (rendu non serrein)\", \"mara\": \"marin\", \"mi\": \"je\", \"mia\": \"je\", \"min\": \"je\", \"mono\": \"argent\", \"ni\": \"nous\", \"nia\": \"nous\", \"okupi\\u011dis\": \"s\'occuper de\", \"ol\": \"que (dans une comparaison)\", \"per\": \"par, \\u00e0 l\'aide de (instrument, moyen de transport...)\", \"pluraj\": \"plusieurs\", \"poste\": \"ensuite, puis\", \"pri\": \"\\u00e0 propos de\", \"pripensoj\": \"r\\u00e9flexion\", \"provizi\": \"doter, fournir, pourvoir\", \"resta\\u0135ojn\": \"reste\", \"restis\": \"rester\", \"sur\": \"sur\", \"tiam\": \"\\u00e0 ce moment l\\u00e0, alors\", \"tion\": \"ce, cela, \\u00e7a\", \"tiuj\": \"ce, cet(te)\", \"tiujn\": \"ce, cet(te)\", \"tuj\": \"imm\\u00e9diatement, sur le champ\", \"vendis\": \"vendre\", \"\\u0109ion\": \"tout, toute chose\", \"\\u0109iuj\": \"chaque, chacun\", \"\\u011din\": \"il, elle\", \"\\u015dajnis\": \"sembler, avoir l\'air\", \"\\u015dipo\": \"bateau\"}}, {\"teksto\": \"Ni ekvelis, kaj iris la\\u016d la vojo de Orientaj Hindioj tra la Persa golfo, kiu estas formita de la bordo de Feli\\u0109a Arabio dekstraflanke, kaj de Persio maldekstraflanke, kaj kies plej ampleksa lar\\u011deco estas sepdek le\\u016dgoj, la\\u016d la komuna opinio. Ekster tiu golfo, la Levanta maro, la sama kiel tiu de Hindioj, estas tre vasta: \\u011di havas sur unu flanko kiel limojn, la bordojn de Abisenio kaj ampleksas kvar mil kvincent le\\u016dgojn en longeco \\u011dis la insuloj Vakvak. Mi unue estis \\u011denata pro tio, kion oni nomas mar\\u00admalsano; sed mi balda\\u016d resani\\u011dis, kaj ekde tiu tempo, mi ne plu suferis pro tiu malsano.\", \"vortaro\": {\"ampleksa\": \"\\u00e9tendu\", \"ampleksas\": \"comporter, comprendre, embrasser\", \"arabio\": \"Arabie\", 