INSERT INTO `tekstoj` (`id`, `ekdato`, `titolo`, `auxtoro`, `arthur_id`, `fonto`, `sono`, `leganto`, `nivelo`, `vortoj`, `kolekto`, `etikedoj`, `enhavo`, `aktiva`) VALUES
('lasumin20b', '2025-07-19 15:50:52', 'Troa scivolo kondukas morten', 'Claude Piron', 6488, 'https://butiko-esperanto-france.org/lectures-faciles/1049-lasu-min-paroli-plu.html', 'https://ikurso.esperanto-france.org/audio/lasumin20b.mp3', 'Jorge Rafael Nogueras', 1490, 1272, NULL, '', '[{\"teksto\": \"A\\u016dskultu, mi tamen \\u015datus scii, kion vi celas finfine. Kio estas via celo ? Vi staris trans la pordo, kaj vi a\\u016dskultis, \\u0109u ne ? Vi suba\\u016dskultis, kiel oni diras. Mi ne dubas, ke interesas vin scii, kiu estas kun mi, kaj pri kio ni parolas. Sed en \\u0109i tiu laboro, en kiu vi kun-agas kun mi, via sinteno estas neallasebla. Iom da digno, mi petas.\", \"vortaro\": {\"a\\u016dskultis\": \"\\u00e9couter\", \"a\\u016dskultu\": \"\\u00e9couter\", \"celas\": \"avoir pour objectif, viser\", \"celo\": \"but, objectif\", \"da\": \"de (apr\\u00e8s une expression de quantit\\u00e9)\", \"digno\": \"dignit\\u00e9\", \"diras\": \"dire\", \"dubas\": \"douter, avoir des doutes\", \"en\": \"dans\", \"estas\": \"\\u00eatre\", \"finfine\": \"finalement, en fin de compte\", \"interesas\": \"int\\u00e9resser\", \"iom\": \"un peu\", \"kaj\": \"et\", \"ke\": \"que (exemple: je sais que\\u2026, il dit que\\u2026)\", \"kiel\": \"comment, comme\", \"kio\": \"quoi, que\", \"kion\": \"quoi, que\", \"kiu\": \"qui\", \"kun\": \"avec\", \"la\": \"le, la, les\", \"laboro\": \"travail\", \"mi\": \"je\", \"ne\": \"non, ne... pas\", \"neallasebla\": \"inacceptable\", \"ni\": \"nous\", \"oni\": \"on\", \"parolas\": \"parler\", \"petas\": \"demander (de faire quelque chose) ; prier (de faire quelque chose)\", \"pordo\": \"porte\", \"pri\": \"\\u00e0 propos de\", \"scii\": \"savoir\", \"sed\": \"mais\", \"sinteno\": \"attitude, comportement\", \"staris\": \"se tenir debout, \\u00eatre debout\", \"suba\\u016dskultis\": \"\\u00e9couter aux portes, \\u00e9couter en cachette\", \"tamen\": \"n\\u00e9anmoins, toutefois, quand m\\u00eame, cependant\", \"tiu\": \"ce, cet(te)\", \"trans\": \"au-del\\u00e0 de, par-del\\u00e0, par-dessus\", \"vi\": \"tu, vous\", \"via\": \"tu, vous\", \"vin\": \"tu, vous\", \"\\u0109i\": \"(indique la proximit\\u00e9)\", \"\\u0109u\": \"est-ce que\\u2026, si (indique l\'interrogation)\", \"\\u015datus\": \"appr\\u00e9cier, aimer\"}}, {\"teksto\": \"Oni subdiris al mi, ke vi transiris al niaj malamikoj. Mi tion ne volis kredi. Kiam vi \\u011dojsaltis vidante min reveni, mi kredis je vi. Kiam vi kisis min kvaza\\u016d amoplene, mi kredis je vi. Kaj \\u0109u vi memoras tiun fojon, kiam vi ploris kaj ploris, \\u0109ar mi \\u0135us tenis min iom malafable ? Mi estis laca kaj \\u0135us agis mal\\u011dentile. Pro laceco oni kelkfoje agas, kiel oni devus ne agi. A\\u016d pro troa zorgado. Nu, mi agis malbone kaj mia sinteno ne estis pravigebla. Kaj vi ploris. Jes, tiun tagon, kiam vi ploris, mi kredis je vi.\", \"vortaro\": {\"agas\": \"agir\", \"agi\": \"agir\", \"agis\": \"agir\", \"al\": \"\\u00e0, vers\", \"amoplene\": \"plein d\'amour\", \"a\\u016d\": \"ou\", \"devus\": \"devoir\", \"estis\": \"\\u00eatre\", \"fojon\": \"fois, occasion\", \"iom\": \"un peu\", \"je\": \"pr\\u00e9position ind\\u00e9finie\", \"jes\": \"oui, d\'accord\", \"kaj\": \"et\", \"ke\": \"que (exemple: je sais que\\u2026, il dit que\\u2026)\", \"kelkfoje\": \"quelquefois, parfois\", \"kiam\": \"quand\", \"kiel\": \"comment, comme\", \"kisis\": \"embrasser\", \"kredi\": \"croire\", \"kredis\": \"croire\", \"kvaza\\u016d\": \"quasi-, comme si\", \"laca\": \"fatigu\\u00e9(e)\", \"laceco\": \"fatigue\", \"malafable\": \"pas aimablement, pas gentiment\", \"malamikoj\": \"ennemi\", \"malbone\": \"mal\", \"mal\\u011dentile\": \"impoliment\", \"memoras\": \"se souvenir, se rappeler\", \"mi\": \"je\", \"mia\": \"je\", \"min\": \"je\", \"ne\": \"non, ne... pas\", \"niaj\": \"notre\", \"nu\": \"allons, bien, eh bien\\u2026 \", \"oni\": \"on\", \"ploris\": \"pleurer\", \"pravigebla\": \"excusable\", \"pro\": \"\\u00e0 cause de, pour\", \"reveni\": \"revenir\", 