('ppp24', '2025-07-19 15:50:52', 'La aĝoj de la homo', 'William Shakespeare', 6697, 'https://butiko-esperanto-france.org/methodes-et-manuels/1451-pasoj-al-plena-posedo.html', NULL, NULL, 1996, 410, NULL, 'paŝoj al plena posedo', '[{\"teksto\": \"LA A\\u011cOJ DE LA HOMO (1)\", \"vortaro\": {\"a\\u011doj\": \"\\u00e2ge\", \"de\": \"de\", \"homo\": \"\\u00eatre humain, homme\", \"la\": \"le, la, les\"}}, {\"teksto\": \"Je la unua jaro, li similas re\\u011don ku\\u015dantan en portlito, \\u0109iuj lin \\u0109irka\\u016dbrakas kaj kisas;\", \"vortaro\": {\"en\": \"dans\", \"jaro\": \"ann\\u00e9e\", \"je\": \"pr\\u00e9position ind\\u00e9finie\", \"kaj\": \"et\", \"kisas\": \"embrasser\", \"ku\\u015dantan\": \"\\u00eatre couch\\u00e9, \\u00eatre \\u00e9tendu, reposer (g\\u00e9sir)\", \"la\": \"le, la, les\", \"li\": \"il, lui, le\", \"lin\": \"il, lui, le\", \"portlito\": \"palanquin, lit portatif\", \"re\\u011don\": \"roi\", \"similas\": \"ressembler\", \"unua\": \"premier, premi\\u00e8re\", \"\\u0109irka\\u016dbrakas\": \"accolade, embrassement, enlacement, \\u00e9treinte, embrasser, enlacer, \\u00e9treindre\", \"\\u0109iuj\": \"chaque, chacun\"}}, {\"teksto\": \"je la dua jaro, li similas porkon, kiu baraktas en malpura\\u0135oj;\", \"vortaro\": {\"baraktas\": \"se d\\u00e9battre, se d\\u00e9mener\", \"dua\": \"second, deuxi\\u00e8me\", \"en\": \"dans\", \"jaro\": \"ann\\u00e9e\", \"je\": \"pr\\u00e9position ind\\u00e9finie\", \"kiu\": \"qui\", \"la\": \"le, la, les\", \"li\": \"il, lui, le\", \"malpura\\u0135oj\": \"ordures, salet\\u00e9s\", \"porkon\": \"porc, cochon\", \"similas\": \"ressembler\"}}, {\"teksto\": \"je la deka jaro, li saltadas kiel kapreolo;\", \"vortaro\": {\"deka\": \"dixi\\u00e8me\", \"jaro\": \"ann\\u00e9e\", \"je\": \"pr\\u00e9position ind\\u00e9finie\", \"kapreolo\": \"chevreuil, chevrette\", \"kiel\": \"comment, comme\", \"la\": \"le, la, les\", \"li\": \"il, lui, le\", \"saltadas\": \"sautiller\"}}, {\"teksto\": \"je la dudeka jaro, li blekas