('ppp28', '2025-07-19 15:50:52', 'La amo de Otelo kaj Desdemona', 'William Shakespeare', 6697, 'https://butiko-esperanto-france.org/methodes-et-manuels/1451-pasoj-al-plena-posedo.html', NULL, NULL, 1449, 716, NULL, 'paŝoj al plena posedo', '[{\"teksto\": \"LA AMO DE OTELO KAJ DESDEMONA\", \"vortaro\": {\"amo\": \"amour\", \"de\": \"de\", \"desdemona\": \"Desd\\u00e9mone\", \"kaj\": \"et\", \"la\": \"le, la, les\", \"otelo\": \"Othello\"}}, {\"teksto\": \"Sen la scio de la patro, Desdemona edzini\\u011dis al la nigrulo Otelo. Eksciante pri tio, la patro, Brabancio, akuzas Otelon anta\\u016d la Do\\u011do. Jago estas la kornedo de Otelo.\", \"vortaro\": {\"akuzas\": \"accuser\", \"al\": \"\\u00e0, vers\", \"anta\\u016d\": \"avant, en avant de, devant\", \"brabancio\": \"Brabantio\", \"de\": \"de\", \"desdemona\": \"Desd\\u00e9mone\", \"do\\u011do\": \"doge\", \"edzini\\u011dis\": \"\\u00e9pouser, se marier\", \"eksciante\": \"apprendre, s\'apercevoir\", \"estas\": \"\\u00eatre\", \"jago\": \"Iago\", \"kornedo\": \"enseigne\", \"la\": \"le, la, les\", \"nigrulo\": \"noir, n\\u00e8gre\", \"otelo\": \"Othello\", \"otelon\": \"Othello\", \"patro\": \"p\\u00e8re\", \"pri\": \"\\u00e0 propos de\", \"scio\": \"\\u00e0 mon insu, \\u00e0 ce que je sais, connaissance, savoir\", \"sen\": \"sans\", \"tio\": \"ce, cela, \\u00e7a\"}}, {\"teksto\": \"\", \"vortaro\": {}}, {\"teksto\": \"DO\\u011cO:\", \"vortaro\": {\"do\\u011do\": \"doge\"}}, {\"teksto\": \"Venigu Desdemonan.\", \"vortaro\": {\"desdemonan\": \"Desd\\u00e9mone\", \"venigu\": \"faire venir\"}}, {\"teksto\": \"\", \"vortaro\": {}}, {\"teksto\": \"OTELO:\", \"vortaro\": {\"otelo\": \"Othello\"}}, {\"teksto\": \"Kornedo, gvidu: vi la lokon scias.\", \"vortaro\": {\"gvidu\": \"guider, diriger\", \"kornedo\": \"enseigne\", \"la\": \"le, la, les\", \"lokon\": \"endroit, lieu\", \"scias\": \"savoir\", \"vi\": \"tu, vous\"}}, {\"teksto\": \"Foriras JAGO kaj servistoj\", \"vortaro\": {\"foriras\": \"partir, renvoyer, cong\\u00e9dier, faire partir, jeter dehors\", \"jago\": \"Iago\", \"kaj\": \"et\", \"servistoj\": \"serviteur, domestique\"}}, {\"teksto\": \"Kaj dume, kiel pure mi konfesas\", \"vortaro\": {\"dume\": \"pour l\'instant, cependant, entre temps, pendant ce temps, en revanche, pourtant, toutefois\", \"kaj\": \"et\", \"kiel\": \"comment, comme\", \"konfesas\": \"inavouable, avouer, accorder, admettre, confesser, se confesser, convenir, d\\u00e9clarer, professer, reconna\\u00eetre\", \"mi\": \"je\", \"pure\": \"purement\"}}, {\"teksto\": \"al Dio la malvirtojn de la sango,\", \"vortaro\": {\"al\": \"\\u00e0, vers\", \"de\": \"de\", \"dio\": \"dieu\", \"la\": \"le, la, les\", \"malvirtojn\": \"l\'oisivet\\u00e9 est la m\\u00e8re de tous les vices, vice\", \"sango\": \"sang\"}}, {\"teksto\": \"do tiel vere al oreloj viaj\", \"vortaro\": {\"al\": \"\\u00e0, vers\", \"do\": \"donc\", \"oreloj\": \"entrer par une oreille et sortir par l\'autre, tendre l\'oreille, ventre affam\\u00e9 n\'a pas d\'oreille, oreille\", \"tiel\": \"ainsi, comme \\u00e7a\", \"vere\": \"vraiment\", \"viaj\": \"ton, votre\"}}, {\"teksto\": \"prezentos mi solene, kiel mi\", \"vortaro\": {\"kiel\": \"comment, comme\", \"mi\": \"je\", \"prezentos\": \"pr\\u00e9senter, introduire\", \"solene\": \"solennellement\"}}, {\"teksto\": \"prosperis ame \\u0109e la sinjorino,\", \"vortaro\": {\"ame\": \"en aimant, amoureusement\", \"la\": \"le, la, les\", \"prosperis\": \"prosp\\u00e9rer, r\\u00e9ussir\", \"sinjorino\": \"dame, madame\", \"\\u0109e\": \"\\u00e0 (comme dans \\u00ab\\u00e0 la table\\u00bb), \\u00e0 c\\u00f4t\\u00e9, pr\\u00e8s de\"}}, {\"teksto\": \"kaj \\u015di \\u0109e mi.\", \"vortaro\": {\"kaj\": \"et\", \"mi\": \"je\", \"\\u0109e\": \"\\u00e0 (comme dans \\u00ab\\u00e0 la table\\u00bb), \\u00e0 c\\u00f4t\\u00e9, pr\\u00e8s de\", \"\\u015di\": \"elle\"}}, {\"teksto\": \"\", \"vortaro\": {}}, {\"teksto\": \"DO\\u011cO:\", \"vortaro\": {\"do\\u011do\": \"doge\"}}, {\"teksto\": \"Rakontu \\u011din, Otelo.\", \"vortaro\": {\"otelo\": \"Othello\", \"rakontu\": \"raconter\", \"\\u011din\": \"il, elle\"}}, {\"teksto\": \"\", \"vortaro\": {}}, {\"teksto\": \"OTELO:\", \"vortaro\": {\"otelo\": \"Othello\"}}, {\"teksto\": \"Min amis \\u015dia patro, invitadis,\", \"vortaro\": {\"amis\": \"aimer\", \"invitadis\": \"inviter\", \"min\": \"je\", \"patro\": \"p\\u00e8re\", \"\\u015dia\": \"elle\"}}, {\"teksto\": \"demandis min pri mia historio\", \"vortaro\": {\"demandis\": \"interroger, demander\", \"historio\": \"histoire\", \"mia\": \"je\", \"min\": \"je\", \"pri\": \"\\u00e0 propos de\"}}, {\"teksto\": \"la\\u016djare: la bataloj, la sie\\u011doj\", \"vortaro\": {\"bataloj\": \"bataille, combat\", \"la\": \"le, la, les\", \"la\\u016djare\": \"annuellement, ann\\u00e9e par ann\\u00e9e\", \"sie\\u011doj\": \"si\\u00e8ge\"}}, {\"teksto\": \"kaj sorto\\u015dan\\u011doj, kiujn mi trapasis.\", \"vortaro\": {\"kaj\": \"et\", \"kiujn\": \"qui\", \"mi\": \"je\", \"sorto\\u015dan\\u011doj\": \"changement de sort\", \"trapasis\": \"traverser\"}}, {\"teksto\": \"Mi \\u011din raportis, jam de l\\u2019 infana\\u011do\", \"vortaro\": {\"de\": \"de\", \"infana\\u011do\": \"enfance\", \"jam\": \"d\\u00e9j\\u00e0\", \"mi\": \"je\", \"raportis\": \"rapporter, annoncer\", \"\\u011din\": \"il, elle\"}}, {\"teksto\": \"\\u011dis la momento, kiam li \\u011din petis.\", \"vortaro\": {\"kiam\": \"quand\", \"la\": \"le, la, les\", \"li\": \"il, lui, le\", \"momento\": \"moment\", \"petis\": \"demander (de faire quelque chose) ; prier (de faire quelque chose)\", \"\\u011din\": \"il, elle\", \"\\u011dis\": \"jusqu\'\\u00e0\"}}, {\"teksto\": \"Jen temis pri fatalaj akcidentoj,\", \"vortaro\": {\"akcidentoj\": \"accident\", \"fatalaj\": \"fatal, fatidique, funeste\", \"jen\": \"voici\", \"pri\": \"\\u00e0 propos de\", \"temis\": \"concerner, toucher, s\'agir de, \\u00eatre question de\"}}, {\"teksto\": \"pri aventuroj sur la ter\\u2019 kaj maro,\", \"vortaro\": {\"aventuroj\": \"aventure\", \"kaj\": \"et\", \"la\": \"le, la, les\", \"maro\": \"mer\", \"pri\": \"\\u00e0 propos de\", \"sur\": \"sur\", \"ter\": \"terre\"}}, {\"teksto\": \"savi\\u011do el la tuj-mortiga bre\\u0109o;\", \"vortaro\": {\"bre\\u0109o\": \"br\\u00e8che\", \"el\": \"hors de, de\", \"la\": \"le, la, les\", \"savi\\u011do\": \"sauvetage\"}}, {\"teksto\": \"kiel min kaptis krudaj malamikoj\", \"vortaro\": {\"kaptis\": \"capturer\", \"kiel\": \"comment, comme\", \"krudaj\": \"\\u00e0 vif, brut, cru, \\u00e9cru, grossier, rudimentaire, rustique\", \"malamikoj\": \"ennemi\", \"min\": \"je\"}}, {\"teksto\": \"kaj vendis en sklavecon; kiel oni\", \"vortaro\": {\"en\": \"dans\", \"kaj\": \"et\", \"kiel\": \"comment, comme\", \"oni\": \"on\", \"sklavecon\": \"esclavage\", \"vendis\": \"vendre\"}}, {\"teksto\": \"absolvis min, kaj jen mi pluvoja\\u011dis:\", \"vortaro\": {\"absolvis\": \"absoudre\", \"jen\": \"voici\", \"kaj\": \"et\", \"mi\": \"je\", \"min\": \"je\", \"pluvoja\\u011dis\": \"continuer le voyage\"}}, {\"teksto\": \"tra kavernegoj kaj dezertaj vastoj,\", \"vortaro\": {\"dezertaj\": \"d\\u00e9sert, d\\u00e9sertique\", \"kaj\": \"et\", \"kavernegoj\": \"grande caverne\", \"tra\": \"\\u00e0 travers\", \"vastoj\": \"immensit\\u00e9s\"}}, {\"teksto\": \"abismoj, krudaj rokoj kaj montegoj\", \"vortaro\": {\"abismoj\": \"ab\\u00eeme, abysse, gouffre, pr\\u00e9cipice, tr\\u00e9fonds\", \"kaj\": \"et\", \"krudaj\": \"\\u00e0 vif, brut, cru, \\u00e9cru, grossier, rudimentaire, rustique\", \"montegoj\": \"grande montagne\", \"rokoj\": \"roc, rocher\"}}, {\"teksto\": \"tu\\u015dantaj la \\u0109ielon \\u2014 jen pri kio\", \"vortaro\": {\"jen\": \"voici\", \"kio\": \"quoi, que\", \"la\": \"le, la, les\", \"pri\": \"\\u00e0 propos de\", \"tu\\u015dantaj\": \"toucher\", \"\\u0109ielon\": \"ciel, cieux\"}}, {\"teksto\": \"li petis min rakonti. Kaj ankora\\u016d\", \"vortaro\": {\"ankora\\u016d\": \"encore\", \"kaj\": \"et\", \"li\": \"il, lui, le\", \"min\": \"je\", \"petis\": \"demander (de faire quelque chose) ; prier (de faire quelque chose)\", \"rakonti\": \"raconter\"}}, {\"teksto\": \"pri l\\u2019 kanibaloj, la antropofagoj\", \"vortaro\": {\"antropofagoj\": \"anthropophage\", \"kanibaloj\": \"cannibale\", \"la\": \"le, la, les\", \"pri\": \"\\u00e0 propos de\"}}, {\"teksto\": \"man\\u011dantaj unu alian, kaj la homoj\", \"vortaro\": {\"alian\": \"autre\", \"homoj\": \"\\u00eatre humain, homme\", \"kaj\": \"et\", \"la\": \"le, la, les\", \"man\\u011dantaj\": \"manger\", \"unu\": \"les un(e)s, certain(e)s\"}}, {\"teksto\": \"kun kapo sub la \\u015dultroj. Tion a\\u016ddi\", \"vortaro\": {\"a\\u016ddi\": \"entendre\", \"kapo\": \"t\\u00eate\", \"kun\": \"avec\", \"la\": \"le, la, les\", \"sub\": \"sous, en-dessous de\", \"tion\": \"ce, cela, \\u00e7a\", \"\\u015dultroj\": \"\\u00e9paule\"}}, {\"teksto\": \"tre emis Desdemona, sed konstante\", \"vortaro\": {\"desdemona\": \"Desd\\u00e9mone\", \"emis\": \"incliner \\u00e0\", \"konstante\": \"constamment\", \"sed\": \"mais\", \"tre\": \"tr\\u00e8s\"}}, {\"teksto\": \"la taskoj de la domo \\u015din fortiris.\", \"vortaro\": {\"de\": \"de\", \"domo\": \"maison\", \"fortiris\": \"arracher, enlever de force\", \"la\": \"le, la, les\", \"taskoj\": \"t\\u00e2che, besogne, devoir (scolaire)\", \"\\u015din\": \"elle\"}}, {\"teksto\": \"Rapide plenuminte ilin, tuj\", \"vortaro\": {\"ilin\": \"ils, elles, eux\", \"plenuminte\": \"accomplir\", \"rapide\": \"rapidement \", \"tuj\": \"imm\\u00e9diatement, sur le champ\"}}, {\"teksto\": \"\\u015di jam revenis kaj avid-orele\", \"vortaro\": {\"jam\": \"d\\u00e9j\\u00e0\", \"kaj\": \"et\", \"revenis\": \"revenir\", \"\\u015di\": \"elle\"}}, {\"teksto\": \"\\u015di sorbis mian temon. Mi rimarkis,\", \"vortaro\": {\"mi\": \"je\", \"mian\": \"mon, ma\", \"rimarkis\": \"remarquer, noter\", \"sorbis\": \"absorber\", \"temon\": \"sujet, th\\u00e8me\", \"\\u015di\": \"elle\"}}, {\"teksto\": \"kaj balda\\u016d mi en horo a\\u016dspicia\", \"vortaro\": {\"a\\u016dspicia\": \"auspicieux, 