INSERT INTO `tekstoj` (`id`, `ekdato`, `titolo`, `auxtoro`, `arthur_id`, `fonto`, `sono`, `leganto`, `nivelo`, `vortoj`, `kolekto`, `etikedoj`, `enhavo`, `aktiva`) VALUES
('ppp30', '2025-07-19 15:50:52', 'Geedziĝo en hodiaŭa maniero', 'Emil Kolozsvári Grandpierre', 6697, 'https://butiko-esperanto-france.org/methodes-et-manuels/1451-pasoj-al-plena-posedo.html', NULL, NULL, 2670, 3110, NULL, 'paŝoj al plena posedo', '[{\"teksto\": \"Ili interkonati\\u011dis en la montaro M\\u00e1tra. La knabino, Anik\\u00f3, apena\\u016d eliris iam el la Somerumejo: taglonge \\u015di \\u015dtopis al si la sciencon en la kapon en la fino de la \\u011dardeno, sub la pirujo, kie neniun \\u015di \\u011denis. \\u015ci lernis juron en la vespera universitato, preparis sin por ekzameno. Kun Dezs\\u00f6, la milda kaj timema scienca esploristo, \\u015di interkonati\\u011dis jam en la unua tago. Ili tagman\\u011dadis \\u0109e la sama tablo, kaj kiomfoje Anik\\u00f3 havis tempon, ili veturis kune inter la montojn en la Trabanto de Dezs\\u00f6, a\\u016d promenadis post la tagman\\u011do sur la ombraj vojoj.\", \"vortaro\": {\"al\": \"\\u00e0, vers\", \"anik\\u00f3\": \"Anik\\u00f3 (pr\\u00e9nom)\", \"apena\\u016d\": \"\\u00e0 peine\", \"a\\u016d\": \"ou\", \"de\": \"de\", \"dezs\\u00f6\": \"Dezs\\u0151 (pr\\u00e9nom)\", \"ekzameno\": \"examen\", \"el\": \"hors de, de\", \"eliris\": \"sortir\", \"en\": \"dans\", \"esploristo\": \"chercheur\", \"fino\": \"habiter au diable vauvert, le meilleur est pour la fin, extr\\u00e9mit\\u00e9, fin, ach\\u00e8vement, arr\\u00eat\", \"havis\": \"avoir\", \"iam\": \"\\u00e0 un moment, un jour, une fois\", \"ili\": \"ils, elles, eux\", \"inter\": \"entre, parmi\", \"interkonati\\u011dis\": \"faire connaissance\", \"jam\": \"d\\u00e9j\\u00e0\", \"juron\": \"droit\", \"kaj\": \"et\", \"kapon\": \"t\\u00eate\", \"kie\": \"o\\u00f9\", \"kiomfoje\": \"combien de fois\", \"knabino\": \"fille, jeune fille\", \"kun\": \"avec\", \"kune\": \"ensemble\", \"la\": \"le, la, les\", \"lernis\": \"apprendre\", \"milda\": \"doux\", \"montaro\": \"montagne, Montagnes Rocheuses\", \"montojn\": \"montagne\", \"m\\u00e1tra\": \"M\\u00e1tra (montagne)\", \"neniun\": \"personne\", \"ombraj\": \"ombrag\\u00e9, ombreux\", \"pirujo\": \"poirier\", \"por\": \"pour, dans le but de\", \"post\": \"apr\\u00e8s\", \"preparis\": \"pr\\u00e9parer\", \"promenadis\": \"vagabonder\", \"sama\": \"le m\\u00eame, la m\\u00eame, identique, pareil(le)\", \"scienca\": \"scientifique\", \"sciencon\": \"science\", \"si\": \"se, s\', soi\", \"sin\": \"se, s\', soi\", \"somerumejo\": \"vill\\u00e9giature\", \"sub\": \"sous, en-dessous de\", \"sur\": \"sur\", \"tablo\": \"table\", \"taglonge\": \"\\u00e0 longueur de temps\", \"tagman\\u011dadis\": \"d\\u00e9jeuner\", \"tagman\\u011do\": \"repas du midi, d\\u00e9jeuner, d\\u00eener\", \"tago\": \"jour\", \"tempon\": \"temps\", \"timema\": \"craintif\", \"trabanto\": \"satellite, Trabant\", \"universitato\": \"universit\\u00e9\", \"unua\": \"premier, premi\\u00e8re\", \"vespera\": \"du soir\", \"veturis\": \"aller (en utilisant un v\\u00e9hicule)\", \"vojoj\": \"chemin, route\", \"\\u0109e\": \"\\u00e0 (comme dans \\u00ab\\u00e0 la table\\u00bb), \\u00e0 c\\u00f4t\\u00e9, pr\\u00e8s de\", \"\\u011dardeno\": \"jardin\", \"\\u011denis\": \"g\\u00eaner, inconforter\", \"\\u015di\": \"elle\", \"\\u015dtopis\": \"bourrer, bonder, boucher, colmater\"}}, {\"teksto\": \"La asignita somerum-tempo de Dezs\\u00f6 pasis pli frue. Anik\\u00f3 restis ankora\\u016d por kelkaj tagoj; \\u015di estis poste devonta meti ekzamenon. Ili interkonsentis, ke tiam kaj tiam, tie kaj tie ili renkonti\\u011dos, du semajnojn post tiu tago, en Budape\\u015dto.\", \"vortaro\": {\"anik\\u00f3\": \"Anik\\u00f3 (pr\\u00e9nom)\", \"ankora\\u016d\": \"encore\", \"asignita\": \"assigner\", \"budape\\u015dto\": \"Budapest\", \"de\": \"de\", \"devonta\": \"devoir\", \"dezs\\u00f6\": \"Dezs\\u0151 (pr\\u00e9nom)\", \"du\": \"deux\", \"ekzamenon\": \"examen\", \"en\": \"dans\", \"estis\": \"\\u00eatre\", \"frue\": \"t\\u00f4t, de bonne heure\", \"ili\": \"ils, elles, eux\", \"interkonsentis\": \"se mettre d\\u2019accord\", \"kaj\"