INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (127, 'Kie la senco tion postulas, enmetu LA en la subajn propoziciojn:', 301, 'La artikolo LA estas preciziga. Ĝi distingas inter ĝenerala termino kaj specifa(j) individua(j) ekzemplero(j) de ĝeneralaĵo. Donu al mi libron = donu al mi iun ajn libron; donu al mi la libron = donu al mi tiun libron (ni ambaŭ scias, pri kiu individua libro temas). Ĉu li alportis monon? = kiom ajn da mono; Ĉu li alportis la monon? = Ĉu li alportis tiun konatan, jam menciitan sumon.', NULL, 'verkado-1', '1', '0');
