INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (128, 'Kie la senco tion postulas, enmetu LA en la subajn propoziciojn:', 301, 'La artikolo LA indikas, ke substantivo estas sola, unika en sia speco. Estas erare diri, ekzemple: ĝi egalas preskaŭ al longeco de ekvatoro, ĉar la mondo havas nur unu ekvatoron, kaj la ekvatoro havas, evidente, nur unu longecon. Oni do diru: ĝi egalas preskaŭ al la longeco de la ekvatoro.', NULL, 'verkado-1', '1', '0');
