INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (134, 'Skribu propoziciojn, tiamaniere uzante jenajn substantivojn:', 307, 'La artikolo LA estas foje uzata anstataŭ poseda adjektivo. En tiaj okazoj, temas pri korpoparto aŭ pri vesto.', 'li rompis al si la kapon (anstataŭ: li rompis al si sian kapon)<br/> li formetis la jakon (anstataŭ: li formetis sian jakon)', 'verkado-1', '1', '0');
