INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (135, 'Uzu tiamaniere jenajn substantivojn:', 307, 'La artikolo <b>LA</b> estas uzata antaŭ singulara substantivo, kiam oni konsideras tiun substantivon ne kiel individuon sed kiel reprezentanton de sia kategorio. <b>LA</b> estas uzata antaŭ plurala substantivo, kiam oni konsideras tiun substantivon kiel la tutaĵon de ĉiuj samkategoriajoj.', 'la amaso juĝas per okulo, kie "la amaso" signifas ĉiujn amasojn.<br/> oni traserĉis la domojn en tiu strato, kiam temas nepre pri ĉiuj domoj en tiu strato. (Oni traserĉis domojn en tiu strato — signifas, ke oni traserĉis iujn, sed ne ĉiujn, domojn.)', 'verkado-1', '1', '0');
