INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (136, 'Laŭ la suba ekzemplo reskribu jenajn propoziciojn, uzante kie necese substantivojn participajn:', 307, '<b>SUBSTANTIVOJ PARTICIPAJ</b> reprezentas ĉiam personon, ili akceptas la sufikson -IN- kiam temas pri ina persono : La persono, kiun mi amas, estas bela = Mia amat(in)o estas bela.', 'Demand<b>ANTO</b> estas persono, kiu demand<b>AS</b>.<br/>Demand<b>INTO</b> estas persono, kiu demand<b>IS</b>.<br/>Demand<b>ONTO</b> estas persono, kiu demand<b>OS</b>.<br/>Demand<b>ATO</b> estas persono, kiun oni demand<b>AS</b>.<br/>Demand<b>ATO</b> estas persono, kiun oni demand<b>IS</b>.<br/>Demand<b>OTO</b> estas persono, kiun oni demand<b>OS</b>.', 'verkado-1', '1', '0');
