INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1383, 162, NULL, '10', 'Pri kio pensis Zlatica, dum ŝi portiĝis de la ondoj?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1384, 162, NULL, '11', 'Kial la patrino ne rimarkis la ŝanĝiĝon de la vetero?', NULL, NULL, NULL, '0', '0');
