INSERT INTO `lecioneroj` (`id`, `leciono_id`, `titolo`, `tipo`, `enhavo`, `ordo`, `unua`, `lasta`, `dauxro`, `android`) VALUES (343, 143, 'Gramatikaj Ekzercoj', 'EKZERCARO', NULL, '4', '0', '0', NULL, NULL);
INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (163, 'Reskribu ĉiufoje la tri propoziciojn en formo de unu korekta kaj eleganta propozicio, ne uzante la vorton KAJ:', 343, NULL,NULL, 'verkado-1', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1389, 163, NULL, '16', 'La patro rigardis la larĝon de la maro. Li karesis la malsekajn harojn de Mare. Mare silente ploris en lia sino.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1390, 163, NULL, '17', 'La motorboato malrapidis. Ĝi eniris la havenon. En la haveno atendis mute amaso da homoj.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1391, 163, NULL, '18', 'Zlatica luktis kontraŭ la ondegoj. Mario ne plu vidis ŝin. Jam delonge li ne aŭdis ŝin.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1392, 163, NULL, '19', 'La fiŝistoj forlasos la havenon. Ili eniris la motorboaton. Ĝi estis atendanta ĉe la bordo.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1393, 163, NULL, '20', 'Zlatica palpkaresos la vizaĝojn de la gepatroj. Ŝi malkovros novajn sulkojn. Ŝi antaŭe ne konis ilin.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1394, 163, NULL, '21', 'Ŝi imagas ridete la vilaĝaninojn. Ili rigardos dimanĉe ŝian patrinon. Tiam la patrino portos tiun kaptukon.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1395, 163, NULL, '22', 'Ŝi havis instinktan antaŭsenton. Ili tro malproksimiĝis de la bordo. Ŝia frato ne rimarkis tion.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1396, 163, NULL, '23', 'La leĝera;boato ĉiam pli kaj pli supreniĝas. Ĝi similas al iu ĉevalo. La ĉevalo estas transsaltanta barojn.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1397, 163, NULL, '24', 'La voĉo de Mario gvidis ŝin. Ŝi naĝis kuraĝe. La ondoj fojfoje superiris ŝian kapon.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1398, 163, NULL, '25', 'La patro forlasis la fenestron. Li eniris la kuirejon. Tie lia edzino okupiĝis pri lavado.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (164, 'Kiom da radikoj vi povas skribi, uzante nur la literojn de la vortoj TEMPESTO SUR MARO?', 343, NULL,NULL, 'verkado-2', '1', '0');
