INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (203, 'Leginte la supran rakonton, respondu jenajn demandojn:', 384, NULL,NULL, 'verkado-2', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1685, 203, NULL, '1', '“Li estis en vera kaĉo.” Kiarigu tiun metaforon.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1686, 203, NULL, '2', '“Sklavumis.” Klarigu kiel la sufikso -UM ŝanĝas (plivastigas) la sencon de la vorto, kaj kial ĝi estas pli pitoreska ol ekzemple “laboris”.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1687, 203, NULL, '3', '“Iom da pundoj”: kial tiu frazo estas pli trafa ol simple “kelkajn pundojn”?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1688, 203, NULL, '4', '“Ŝtopi vian kvakantan faŭkon.” Esprimu tion, unue ĝentile, poste per similaj malĝentilaj frazoj.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1689, 203, NULL, '5', '“Vi ekŝrumpas kiel pikita baloneto.” Elpensu simile trafajn komparojn, kompletigante la subajn propoziciojn:<br/>Li gapis stulte kiel ...<br/>Ŝi grumblis kiel ...<br/>Li estis aplomba kiel ...<br/>Ŝi lamentis kiel ...<br/>Ĝi staris rekta kiel ...', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1690, 203, NULL, '6', '“Zorgumis.” Kian impreson ĉi tie liveras la sufikso -UM?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1691, 203, NULL, '7', '“Senbruigi”:kiom da sinonimaj vortoj kaj esprimoj vi povas listigi?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1692, 203, NULL, '8', '“La dommastrino tujtuj fermtiris la pordon.” Ripetado de la radiko tuj emfazas ĝin. Ĉu vi konas aliajn tiajn duoblajojn?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1693, 203, NULL, '9', '“Kriĉi kiel lokomotiva fajfilo.” Elpensu simile trafajn sonkomparojn: muĝi kiel ...; murmuri kiel ...; knari kiel ...; kraki kiel ...; boji kiel ...; pepi kiel ...; bleki kiel ...; zumi kiel ...; ĝemi kiel ...', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1694, 203, NULL, '10', '“Mi bone remburis la prezon.” Klarigu tiun metaforon.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (204, 'Jen kelkaj proverboj. Ĉu via komprenas iliajn signifojn? Klarigu ilin:', 384, NULL,NULL, 'verkado-2', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1695, 204, NULL, '11', 'Tio tuŝis min kiel akvo anseron.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1696, 204, NULL, '12', 'Ĝi donis al ili urtikon en la kalsonon.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1697, 204, NULL, '13', 'Esti inter martelo kaj amboso.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1698, 204, NULL, '14', 'Danceti kiel kato sur fornoplato.', NULL, NULL, NULL, '0', '0');
