INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1700, 204, NULL, '16', 'Kiu min tuŝas, min sentas.', NULL, NULL, NULL, '0', '0');
