INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (206, 'En la “Ekzercaro”, Zamenhof citis 52 senchavajn vortojn formitajn el la radiko SAN per aldono de sufiksoj kaj vortfinaĵoj. Al la listo li aldonis “k.t.p.” Kiom da vortoj vi povas per sufksoj kaj vortfinaĵoj formi el jenaj radikoj:', 385, NULL,'Sano, sana, sane, sani, sanu, saniga, saneco, sanilo, sanigi, saniĝi, sanejo, sanisto, sanulo, malsano, malsana, malsane, malsani, malsanulo, malsaniga, malsaniĝi, malsaneta, malsanema, malsanulejo, malsanulisto, malsanero, malsaneraro, sanigebla, sanigisto, sanigilo, resanigi, resaniĝanto, sanigilejo, sanigejo, malsanemulo, sanilaro, malsanaro, malsanulido, nesana, malsanado, sanulajo, malsaneco, malsanemeco, saniginda, sanilujo, sanigilujo, remalsano, remalsaniĝo, malsanulino, sanigista, sanigilista, sanilista, malsanulista k. t. p.', 'verkado-2', '1', '0');
