INSERT INTO `lecioneroj` (`id`, `leciono_id`, `titolo`, `tipo`, `enhavo`, `ordo`, `unua`, `lasta`, `dauxro`, `android`) VALUES (386, 150, 'Verkado', 'EKZERCARO', NULL, 5, 0, 0, NULL, NULL);
INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (209, 'Traduku la antaŭlastan alineon de la legaĵo en vian nacian lingvon. ', 386, '“Aranĝite, knaboj!” diris Benzo. “Nu kio? Iom da tondro kaj hajlo en la policejo, sed tio tusis min kiel akvo anseron. Nun ja ili transprenis la zorgon, kaj ĝi donis al ili urtikon en la kalsonon! Kion fari? Ili devis eltiri la leonon kun helpo de cirkaj dresistoj, kaj ili loĝigis ĝin ĉe la cirko. Tamen la besto ja restis mia! Tion ili ne povis ŝanĝi. Ili akuzis min pri rompo de l’ paco; mi kontraŭatakis, ke ili senrajte forprenis mian propraĵon. Diable, do mutigis ilin! Ili estis inter martelo kaj amboso – ne sciis kion fari. Mi vidis ilin danceti kiel kato sur fornoplato. Fine mi konsentis intertrakti, kaj ili vendis la leonidon al la zo’o – kredu, mi bone remburis la prezon! Jes, knaboj, lasu min fari: mi scias min aranĝi. En mia cerbo ne kreskas herbo. Kiu min tuŝas, min sentas!”',NULL, 'verkado-2', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1726, 209, NULL, '38', 'Jen via traduko:', NULL, NULL, NULL, '0', '0');
