INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (210, 'Leginte la supran rakonton, respondu jenajn demandojn:', 390, NULL,NULL, 'verkado-2', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1727, 210, NULL, '1', '“Kalvaj arboskeletoj.” En tiu frazo estas duobla metaforigo. Klarigu ambaŭ.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1728, 210, NULL, '2', '(a) “Vi vidos kaplevante grizan kraboforman kajton”; (b) “Mi legis hazarde fremdlingvan libron.” En ambaŭ propozicioj la pozicio de la adverbo kaŭzas nenecesan ambiguecon. Klarigu la kaŭzon de tio, kaj reskribu ambaŭ propoziciojn senambigue.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1729, 210, NULL, '3', '“La kajtoj aspektas kompatinde deprimitaj.” En tiu propozicio troviĝas ekzemplo de personigo. Klarigu ĝin.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1730, 210, NULL, '4', '“Salikoj kaj persikujoj.” Kiom da aliaj arboj vi konas?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1731, 210, NULL, '5', '“Mi konsideris ĝin ludo de sentaŭgaj knabaĉoj.” Klarigu la nominativon de la vorto ludo.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1732, 210, NULL, '6', 'Skribu la vortojn en la tria alineo de la legaĵo formitajn per sufiksoj, kaj analizu ilin.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1733, 210, NULL, '7', '“Sur la granda sidilo apogis sin skeleto de papiliforma kajto.” Kial estas necesa la vorto sin, kaj kion ĝi indikas?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1734, 210, NULL, '8', '“En la ĉambro diversaj objektoj estis entenataj” estas pasiva propozicio, kiu aktive fariĝus: “La ĉambro entenis diversajn objektojn.” Skribu aktive jenan pasivan propozicion: “Li estis ĵus okupata alglui al ili ruĝajn paperpecojn.”', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1735, 210, NULL, '9', '“Mi tuj etendis miajn manojn kaj rompis flugilon de la papilio.” Kiel oni ŝanĝus la sencon de tiu propozicio, se oni enmetus la artikolon la antaŭ la vorto flugilo?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1736, 210, NULL, '10', '“Mi konis ankaŭ rimedon por ripari mian pasintan eraron: donaci al li kajton.” Klarigu la funkcion de la dupunkto (:) en tiu propozicio; per kiuj du vortoj oni povus esprimi la saman funkcion, kaj kio estas la akceptata mallongigo de tiuj vortoj?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1737, 210, NULL, '11', '“Mi kredis, ke li tiel diros.” Kial la tempo de dir- estas futura, kvankam la tuta rakonto jam antaŭlonge okazis?', NULL, NULL, NULL, '0', '0');
