INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1739, 211, NULL, '13', 'Kiam li laboras? (Hodiaŭ... ĉiutage...)', NULL, NULL, NULL, '0', '0');
