INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1741, 211, NULL, '15', 'Kiom li amas? (Multe... tutkore...)', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1742, 211, NULL, '16', 'Per kio li batalas? (Glave... pugne...)', NULL, NULL, NULL, '0', '0');
