INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (215, 'Leginte la supran artikolon, respondu jenajn demandojn:', 396, NULL,NULL, 'verkado-2', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1755, 215, NULL, '1', 'En la artikoto la aŭtoro uzas, kvankam la okazaĵoj estas jam ĉiuj pasintaj, sufiĉe ofte la nuntempon (-as); kelkfoje la nuntempa kaj pasinta tempoj proksime intermiksiĝas. Ekzemple: “Maria Skłodowska aplikas novajn metodojn de mezurado de la mistera fenomeno. Baldaŭ ŝi malkovris, ke ...”Ĉu vi povas klarigi tian uzadon de la nuntempo, kiam temas pri pasintaĵoj?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1756, 215, NULL, '2', '“En la uraniaj ercoj troviĝas nekonata ĝis nun en la scienco kemia elemento.” En tiu propozicio estas adjektiva frazo priskribanta la vorton elemento: “nekonata-ĝis-nun-en-la-scienco”. Tiu vortordo disigas la vorton kun adjektiva finalo (nekonata) disde ĝia substantivo (elemento); ĝi tamen metas la ĉefan vorton en la fino de la propozicio, kaj ĝi atentigas, ke la tuta frazo devas esti konsiderata kiel adjektiva tutaĵo. Oni povus skribi la propozicion ankaŭ jene: “En la uraniaj ercoj troviĝas kemia elemento ĝis nun nekonata en la scienco”; tiu vortordo apudigas la verbon kaj ĝian subjekton. Dank''al la libera vortordo de Esperanto, elekto estas afero de stilo, kaj oni havas diversajn eblecojn akcenti kaj elegantigi sian esprimadon. Trovu en la artikolo minimume du aliajn ekzemplojn de tiaj frazoj, kiuj disigas adjektivon kaj ĝian substantivon.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1757, 215, NULL, '3', '“La lavanga progreso de la nuklea fiziko”. Klarigu la tiean metaforon.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1758, 215, NULL, '4', 'Klarigu la diferencon inter radioaktiveco kaj radioaktivado.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1759, 215, NULL, '5', '“El kie do fontis la energio de ĉi tiuj misteraj, nevideblaj radioj, efikantaj sur la kliŝon kaj jonizantaj la aeron.” Reskribu tiun propozicion, ne uzante la participajn finaĵojn.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1760, 215, NULL, '6', '“Tiutempaj fizikistoj.” Donu sinonimon de “tiutempaj”.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1761, 215, NULL, '7', '“La kunlaboron finigos lia tragika morto.” Klarigu la uzon de -ig en tiu propozicio.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1762, 215, NULL, '8', '“Tio estis penego tute eksterordinara kaj pro la ĝeneralaj malfacilaĵoj kaj pro la escepte malfacilaj laborkondiĉoj.” Klarigu la signifon de la duobla kaj. Per kiu alia vortparo oni povas esprimi la saman signifon?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1763, 215, NULL, '9', '“... konfirmis ĉi tiun hipotezon eksperimente.” Anstataŭ la vorto eksperimente, skribu samsignifan adverban frazon.', NULL, NULL, NULL, '0', '0');
