INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (221, 'Leginte la supran rakonton, respondu jenajn demandojn:', 402, NULL, NULL, 'verkado-2', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1806, 221, NULL, '1', '“... iu reĝfilino estas ŝtelita de ciganoj, ŝi suferas kaj mortas ...” Kial ĉi tie estas uzita la nuntempo (-as)? La unua parto de la propozicio estas pasiva, reskribu ĝin aktive. Donu sinonimon de reĝfilino.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1807, 221, NULL, '2', '“La barbo estas farita el kapra vosto.” Skribu ankaŭ tiun propozicion aktive.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1808, 221, NULL, '3', '“Kiel eble plej rapide.” Skribu propoziciojn, uzante kiel eble plej kun jenaj vortoj : EVIDENTA; IMITINDA; NEGLEKTE; ORIGINALA; ARTIFIKE.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1809, 221, NULL, '4', '“La salono apenaŭ ne detruiĝis...” skribu unuvortan sinonimon de <i>apenaŭ ne</i>.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1810, 221, NULL, '5', '“Mi estas bone, kiel vi fartas?” Jen ekzemplo de idiotisma  Esperanto. Gramatike, la unua frazo devus legiĝi: “Al mi estas bone” (kial?), sed ofta kaj kutima uzado de la plena frazo kaŭzas, ke la frazo de la aŭtoro restas tute komprenebla kaj natura. Kial li ne povis skribi “Mi estas bona”?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1811, 221, NULL, '6', '“... rigardis min kun certa dubo.” Jen denove idiotismo, sufiĉe disvastiĝinta. Ĉi tie la vorto certa estas uzita anstataŭ iom da aŭ ia. Kion signifas fundamente la vorto <i>certa</i>?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1812, 221, NULL, '7', '“Mi frapis la teleron je la plankon kaj la kravaton disŝiris je pecoj.” Laŭeble klarigu la diferenoon signifan inter la du uzoj de “je”.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1813, 221, NULL, '8', '“Ŝia krio infektis ĉiujn ceteraj...” Klarigu la metaforon.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1814, 221, NULL, '9', '“... la tuta salono eksimilis al vivanta maro.” Klarigu tiun similigon.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1815, 221, NULL, '10', 'Kial la vortoj “kara amiko”, en la fina parolo de la ĉefa respondeculo, estas skribitaj inter citiloj?', NULL, NULL, NULL, '0', '0');
