INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1911, 234, NULL, '4', '“La okrokolora dezerto envolvas la verdantan insuleton, kiel smeraldon en ĝia juvelujo.” Klarigu la tie uzitan similaĵon.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1912, 234, NULL, '5', 'En la dua alineo estas uzitaj 7 neparticipaj afiksoj. Klarigu ilian signifon, kaj skribu po 5 aliajn, en kiuj la afiksoj estas korekte uzataj.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1913, 234, NULL, '6', '“... sin vidigi kiel la Salomonon de la dezerto.” Tio estas ekzemplo de speciala metaforo, kiun oni povas nomi antonomazio: ĝi estas uzado de konata propra nomo por reprezenti la kvalito(j)n, per kiu(j) la persono estas konata. Donu aliajn ekzemplojn de tiu figuro.', NULL, NULL, NULL, '0', '0');
