INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1991, 247, NULL, '52', 'Skribu detale', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (248, 'Vi legis supre tri opiniojn pri la diversaj aĝoj de la homo. Kiu el ili ŝajnas al vi plej trafa? Plej sprita? Plej serioza? Ĉu vi konsentas kun ili? Se ne, kial ne?', 422, NULL,NULL, 'verkado-2', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1992, 248, NULL, '53', 'Pravigu ĉiufoje vian respondon.', NULL, NULL, NULL, '0', '0');
