INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (249, 'Leginte la suprajn eltiraĵojn, respondu jenajn demandojn:', 426, NULL,NULL, 'verkado-2', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1993, 249, NULL, '1', 'Supre oni trovas multajn nomojn de arboj, plantoj, birdoj kaj bestoj. Laŭeble identigu ilin, ne nur laŭ iliaj nacilingvaj nomoj, sed ankaŭ per birdoj aŭ eĉ specimenoj.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1994, 249, NULL, '2', 'En la supra ĉapitro, kaj tra la tuta libro, la awtoro uzas, apud sciencaj nomoj, ankaŭ popolajn nomojn por iuj floroj. Sube ni listigas, maldekstre, plurajn popolajn nomojn, kaj dekstre la sciencajn nomojn, de iuj floroj kaj plantoj. Ĉu en via nacilingvo ili havas nomojn popolajn, kaj ĉu ili estas similaj al la esperantaj?<br/>ezokfaŭko&nbsp;=>&nbsp;linario<br/>herbo de Sankta Johano&nbsp;=>&nbsp;sedo<br/>hundolango&nbsp;=>&nbsp;Cynoglossum officinale<br/>kaskofloro&nbsp;=>&nbsp;akonito<br/>kavalirsprono&nbsp;=>&nbsp;delfinio<br/>leondento&nbsp;=>&nbsp;leontodo<br/>neforgesumino&nbsp;=>&nbsp;miozoto<br/>neĝborulo&nbsp;=>&nbsp;galanto<br/>nudaj virgulinoj&nbsp;=>&nbsp;kolĉiko aŭtuna<br/>orpluvo&nbsp;=>&nbsp;laburno<br/>terpiro&nbsp;=>&nbsp;tubera helianto<br/>vergo d''oro&nbsp;=>&nbsp;solidago', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1995, 249, NULL, '3', '“Bovina kuko” kaj “ĉevala piro” estas evidente uzitaj figure. Klarigu.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1996, 249, NULL, '4', 'Distingu inter “trideko da jaroj” kaj “tridek jaroj”.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1997, 249, NULL, '5', '“En fora angulo.” Donu sinonimon de “fora”.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1998, 249, NULL, '6', '“La betulo, kiu vere nin turmentas.” Klarigu tiun personigon de la betulo.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1999, 249, NULL, '7', '“Tiomgrade.” Reskribu tiun vorton kiel trivortan frazon samsignifan.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2000, 249, NULL, '8', '“... portis aere la flugilhavajn ulmofruktojn.” Reskribu la vorton aere kiel trivortan frazon samsignifan.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2001, 249, NULL, '9', 'Klarigu la diferencon inter “gustis” kaj “gustumis”.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2002, 249, NULL, '10', '“Kaj ni imagis al ni la amatan parkon, al si mem tute lasitan, iom post iom transformita al unu grandega arbaro ...” Klarigu, kial la vorto <i>transformita</i> estas nominativa.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2003, 249, NULL, '11', 'En la sesa alineo trovu po unu ekzemplo de personigo kaj metaforo.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2004, 249, NULL, '12', 'Analizu ĉiujn vortojn el la naŭa alineo, kiuj estas formitaj per afiksoj.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2005, 249, NULL, '13', '“La solidaj hoketoj efektive prizorgas, ke la infloreskoj de la lapo kiel kaŝpasaĝeroj estu longe forportataj sur la felo de besto, for al lokoj, kie estas pli da ŝancoj, por ke la semo ĝermu, ol en densa kreskaĵaro de larĝfoliaj lapoj.” Klarigu la du uzojn de la u-finaĵo de la verbo.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2006, 249, NULL, '14', 'ODISEO: “... estis virsafoj bonnutritaj kaj denslanaj, belaj kaj grandaj, kun violkolora lano; senbrue mi tiujn kunligis per flekseblaj branĉetoj, sur kiuj kutimis dormi la Kiklopo, la monstrulo, inklina al maljustaĵoj, kaj ĉiufoje mi prenis tri virŝafojn kune; ĉiu meza portis viron, kaj ambaŭ aliaj iris ambaŭflanke por ŝirmprotekti la kunulojn. Tiel do ĉiufoje tri virŝafoj portis unu viron; sed mi — ĉar estis unu virŝafo multsupere plej granda inter ĉiuj ŝafoj — ĉirkaŭprenis ties dorson kaj sub lia harkovrita ventro kurbigante min, mi tiel restis pendanta; kaj envolvinte la manojn en la densegan lanon mi tenadis min konstante al ĝi kun persistema animo... Ilia mastro, turmentata de teruraj doloroj, palpadis la dorsojn de ĉiuj virŝafoj rekte starantaj; sed tion ne rimarkis la stultulo, ke miaj kunuloj estis ligitaj sub la brustoj de la lankovritaj virŝafoj. Laste el ĉiuj iris eksteren mia virŝafo, ŝarĝite per sia lano kaj per mi kun miaj inteligentaj pensoj... Kiam ni estis venintaj je malgranda distanco de la kaverno kaj la barilo, tiam unue mi malligis min mem de sub la virŝafo kaj tiam mi liberigis miajn kunulojn.” (“Odusseias de Homeros”, tradukita de W. J. T. Manders.)<br/>Ĉu vi konas, aŭ povas eltrovi, aliajn aventurojn de Odiseo?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2007, 249, NULL, '15', '“Tra tiuj jaroj oni povis observi la progresantan fronton de la balzaminaj trupoj, ĝis fine ili nun okupadis la tutan hortikulturejon.” Kiel nomiĝas tiu figuro? Klarigu ĝin.', NULL, NULL, NULL, '0', '0');
INSERT INTO `lecioneroj` (`id`, `leciono_id`, `titolo`, `tipo`, `enhavo`, `ordo`, `unua`, `lasta`, `dauxro`, `android`) VALUES (427, 157, 'Gramatikaj Ekzercoj', 'EKZERCARO', NULL, '4', '0', '0', NULL, NULL);
INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (250, 'En la subaj alineetoj, pluraj verboj, kursive presitaj, estas aktivaj. Reskribu la propoziciojn tiel, ke tiuj verboj estu pasivaj.', 427, NULL,NULL, 'verkado-2', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2008, 250, NULL, '16', 'Sed se ekzistos la plej alta institucio, kiun la tuta esperantistaro <i>elektis</i>, tiam miaj manoj <i>liberiĝos</i>, kaj ĉion, kion mi trovos utila, mi povos kuraĝe proponi al la decido de tiu institucio, kaj ĉion, kion tiu institucio decidos, <i>oni povos kuraĝe fari</i>, kaj neniu havos la rajton protesti aŭ disputi.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2009, 250, NULL, '17', 'Oni <i>mortigis</i> multe da personoj. Kompreneble oni <i>atingis</i> nenian rezultaton, ĉar la bone armita kaj senkompata soldataro montriĝis multe pli forta ol la malfeliĉa tute senarma popola amaso.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2010, 250, NULL, '18', 'Oni <i>devas</i> diri, ke, kiam la firmo ĉesos pagadi al mi la minimumon, tiam la kontrakto perdas sian leĝan valoron kaj mi fariĝas tute libera kaj mi povas eldonadi mem ĉiujn miajn verkojn, ne sole kiujn mi <i>faros</i> sed ankaŭ kiujn mi <i>faris</i>.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2011, 250, NULL, '19', 'Se mi bezonos presi iajn artikolojn pri Esperanto, mi penos ĉiam fari tion ĉi en tiuj gazetoj, kiujn Hachette <i>eldonos</i>.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2012, 250, NULL, '20', 'Persono, kiu iam <i>ellernis</i> Esperanton, devas havi plenan certecon, ke neniu vorto, kiun li ellernis, iam perdos sian valoron kaj ke lin neniam <i>malkomprenos</i> aliaj esperantistoj.', NULL, NULL, NULL, '0', '0');
