INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2022, 254, NULL, '1', 'Al la komikeco de la supra rakonto kontribuas ankaŭ la stilo. Ekzemple, la aŭtoro ofte troigas detalojn per pompa, eĉ bombasta, frazelekto. Trovu en la rakonto plurajn ekzemplojn de tia troigo.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2023, 254, NULL, '2', 'Inverse, la aŭtoro utiligas ankaŭ maltroigon komikefekte. Trovu ekzemplojn de tio.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2024, 254, NULL, '3', 'Citu ekzemplojn, kie la uzo de -eg kaj -ad aldonas al la komikeco de la rakonto (komencu jam ĉe la titolo!).', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2025, 254, NULL, '4', 'Kia person estas la patro? la patrino? la avino? Fabiano? la aŭtoro?', NULL, NULL, NULL, '0', '0');
