INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2142, 261, NULL, '1', '“... dum la ŝtorma nokto de la 25a de januaro, 1759.” Klarigu la neceson ambaŭfoje uzi la artikolon “la”.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2074, 261, NULL, '2', 'Kial la aŭtoro uzis la esprimon “eĉ anglaj tradukoj”? Kial la ekzisto de tradukoj anglaj estas surpriza?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2075, 261, NULL, '3', 'Kial la aŭtoro skribis: “Evidente la enhavo pli ol la lingvo aŭ la stilo rekomendis la poezion de Burns al milionoj en multaj landoj”? Ĉu tiu opinio estas nepre tutprava? Kial?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2076, 261, NULL, '4', 'Kiel diferencas inter si dorlothundo kaj ŝafhundo?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2077, 261, NULL, '5', '“Sed lia koro ...emis venki lian kapon.” Klarigu la signifon de tio, per esprimoj nefiguraj.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2078, 261, NULL, '6', 'Skribu frazojn anstataŭ jenaj adverboj: krudmane; trankvilanime; ĉiujare.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2079, 261, NULL, '7', 'Donu sinonimon de <i>trinkejo</i>.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2080, 261, NULL, '8', '“Malamante hipokritecon, Burns verkis satirojn...” Anstataŭ la participa frazo, skribu frazon samsignifan, kiu komenciĝas per konjunkcio.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2081, 261, NULL, '9', '“La rang''— la homon stampas nur, Sed estas hom'' la oro!” Klarigu la supran metaforon.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2082, 261, NULL, '10', 'Difinu kiel eble plej precize la vorton <i>simpatio</i>.', NULL, NULL, NULL, '0', '0');
