INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (266, 'Ĉiu lando, kiel ĉiu individuo, havas sian plej amatan poeton. Skribu pri la amata poeto de via lando. (Se vi estas skoto, respondu anstataŭe: ĉu vi konas kaj amas alian poeton krom Burns?). Trovu laŭeble, kiuj poemoj lial estas jam tradukitaj en Esperanton, kaj lernu minimume unu el ili parkere.', 440, NULL,NULL, 'verkado-1', '1', '0');
