INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (273, 'Leginte la supran rakonton, respondu jenajn demandojn:', 450, NULL,NULL, 'verkado-2', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2160, 273, NULL, '1', 'Iasence la aŭtoro ja sugestias pesilon por mezuri la homvivon kontraŭ la ĉielaj radioj. Kiasence?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2161, 273, NULL, '2', 'Evidente la aŭtoro prezentas du soldatojn, kiel li skribas, “tiel diversajn kaj similajn”, eble pli similajn ol diversajn. La ripetado de detaloj de la du malamikoj estas unu el la rimedoj, per kiuj li volas sentigi sian temon. Sed okazas ankaŭ ripetiĝoj vortaj, kiuj havas fortan efikon. Trovu kelkajn el tiuj vortaj ripetiĝoj, kaj analizu ilin.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2162, 273, NULL, '3', '“Iu el la du enmeditiĝis kaj ĝustatempe ne plilongigis per la englutita enspiro ties mallongan vivon.” Kritiku la pozicion de “ĝustatempe” en tiu propozicio.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2163, 273, NULL, '4', 'Kiel Pierre kaj Peter malsimilis inter si? Kiel ili similis? Kiuj, laŭ via opinio, estis la pli gravaj vivfaktoroj — la malsimilaĵoj aŭ la similaĵoj? Kiuj faktoroj estis, laŭ la opinio de la aŭtoro, la pli gravaj? Kiel li sukcesis sentigi sian opinion?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2164, 273, NULL, '5', 'Ĉu Pierre Dubois batalis por la libero?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2165, 273, NULL, '6', 'Pri kio Pierre pensis, dum li ekbruligis sian lastan pipon? Kion tiuj pensoj sciigas al ni pri la homoj soldatiĝintaj dum milito?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2166, 273, NULL, '7', 'Kiel reagis Pierre, kiam li ekvidis tiel proksima Peteron Debau?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2167, 273, NULL, '8', 'La rakonto ne finiĝas kun la morto de la du soldatoj: ni sciiĝas pri la efiko de tiu duobla morto. Kion tiu efiko sentigas pri (a) la milito? (b) la vivo?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2168, 273, NULL, '9', 'Al kio komparas la aŭtoro la du trovitajn skeletojn? Kial tiu komparo estas (intence) ironia?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2169, 273, NULL, '10', 'Kio estas la fina efekto de la rakonto sur la leganton? Analizu tiun efekton.', NULL, NULL, NULL, '0', '0');
INSERT INTO `lecioneroj` (`id`, `leciono_id`, `titolo`, `tipo`, `enhavo`, `ordo`, `unua`, `lasta`, `dauxro`, `android`) VALUES (451, 161, 'Gramatikaj Ekzercoj', 'EKZERCARO', NULL, '4', '0', '0', NULL, NULL);
INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (274, 'La subaj propozicioj enhavas po unu el jenaj figuroj: metaforo; similigo; ironio; antonomazio; kalemburo. Trovu kaj analizu la figurojn.', 451, NULL,NULL, 'verkado-1', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2170, 274, NULL, '11', 'La miljara paco estis rimarkinda fenomeno en si mem.', NULL, NULL, NULL, '0', '0');
