INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (275, 'La poeto Eŭgeno Miĥalski verkis poemon sub titolo “Paraleloj”, kies unua strofo legiĝas jene:<br/>... Novo — malnovo,<br/>eterno — momento,<br/>potenco — senpovo,<br/>feliĉo — turmento.<br/>Kiel vi vidas, temas pri vortparoj, kiuj estas antonimaj. Vi vidas krome, ke la unua kaj tria paroj rimas, ankaŭ la dua kaj kvara. Sube vi trovas 24 vortojn; ill estas la vortoj de tri aliaj strofoj en la poemo, sed tute misordigitaj. Laŭ tiuj indikoj, ĉu vi povas rekonstrui la tri strofojn?', 451, 'KARESO SINCERO INFERO MILDECO PROMESO ĴALUZO SUFERO EKFLAMO FIDELO KOMPATO BEATO EDENO RIBELO INSULTO TRANKVILO ADORO KONFIDO MALAMO FI-TROMPO KRUELO ADULTO KONSTANTO AMUZO ĴURROMPO',NULL, 'verkado-2', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2175, 275, NULL, '16', 'Listigu la parojn kaj ordigu ilin', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (276, 'Korektu la subajn propoziciojn, tiel ke ili diru tion, kion la aŭtoroj evidente intencis.', 451, NULL,NULL, 'verkado-1', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2176, 276, NULL, '17', 'Estas notinde, ke la eldonejo akceptis Esperanton, kvankam ĝis nun ĝi estis kontraŭ nia lingvo sekve de la multa pruvmaterialo, kiun nia samideano povis disponigi.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2177, 276, NULL, '18', 'La patrinoj faras sian eblon forigi la infanojn, kokojn kaj ceterajn dombestojn de sur la reloj.', NULL, NULL, NULL, '0', '0');
