INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (279, 'Leginte la supran rakonton, respondu jenajn demandojn:', 456, NULL,NULL, 'verkado-2', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2197, 279, NULL, '1', 'Anikó uzas multajn familiarajn esprimojn, kliŝiĝintajn en la junpopola parolado. Ekzemple, por esprimi sian entuziasmon ŝi uzas interalie la adverbojn: animale, beste, brute. Tiaj esprimoj jam ne posedas evidentan logikecon en la uzado; tamen origine ili sendube devenis de metafora impreso pri ilia fono. Ĉu vi povas sugesti la originon de la uzado de la supraj adverboj?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2198, 279, NULL, '2', 'Ankaŭ “Heninde!” havas bestan originon; kion Anikó kutime esprimas per ĝi?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2199, 279, NULL, '3', 'Alia karakterizo de la paroloj de Anikó estas trafaj similigoj. Eltiru kelkajn el ili, kaj diru, ĉu ili estas trafaj en la kunteksto.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2200, 279, NULL, '4', 'Kion ŝi celas, kiam ŝi diras: “... estos krako en via melono”?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2201, 279, NULL, '5', 'Skribu en “normala” lingvaĵo la propozicion: “Poŝremburo estas luna, eĉ telekupron mi ne havas, telefoni al iu sciuro”.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2202, 279, NULL, '6', 'Klarigu la signifon de: “Per sia ega muzelo li kovradis sian kretenecon, sed tiel grandan muzelon ankaŭ li ne povis gravmieni, ke tiu ne elŝovu sin el sub ĝi”.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2203, 279, NULL, '7', 'Kiun vorton Anikó kutime uzas anstataŭ ulo aŭ persono?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2204, 279, NULL, '8', 'Kiel diferencas la sinteno de Anikó kiam ŝi ekvidas la loĝejon de Dezsö, de ŝia sinteno, kiam ŝi ekloĝas tie? Per kiaj familiaraĵoj ŝi karakterizas ĝin ambaŭfoje?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2205, 279, NULL, '9', 'Kion esprimas Anikó per la demandoj: Kio la breĉo? Ĉu krevis la digo?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2206, 279, NULL, '10', '“Skrupedantaĵojn” El kiuj du radikoj, kunpremitaj, estas tiu kunmetaĵo farita?', NULL, NULL, NULL, '0', '0');
