INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2207, 280, NULL, '11', 'Duŝilingo pale pendas, kvazaŭ luno,<br/>Solece super urb''ardanta forne. (Newell)', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2208, 280, NULL, '12', 'Jen — ĝi brilas, mia 1uno,<br/>Virgomaska amlucerno,<br/>Nuda mamo el latuno,<br/>Iritilo de l''eterno ... (Hohlov.)', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2209, 280, NULL, '13', 'Envolvas ĉion sorĉa ret'':<br/>Silent'' viola. (Tárkony.)', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2210, 280, NULL, '14', 'Vian nigran hararon luno pudras<br/>nun per polvo arĝenta. (Schauhuber.)', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2211, 280, NULL, '15', 'Maro, zorga dommastrino,<br/>faldas re kaj re sen fino<br/>susure silkajn ondojn. (Maura.)', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzercoj` (`id`, `komando`, `lecionero_id`, `komando_detalo`, `ekzemplo`, `typo`, `x2u`, `korektebla`) VALUES (281, 'Korektu la subajn propoziciojn, tiel ke ili esprimu tion, kion la aŭtoroj evidente intencis:', 457, NULL,NULL, 'verkado-1', '1', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2212, 281, NULL, '16', 'Nu, ĉu vi ne estas ankoraŭ kontentigita, ho postulino?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2213, 281, NULL, '17', 'Ĝojigu la koron de via amiko sendante al li leteron sur tiu ĉi poŝtkarto.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2214, 281, NULL, '18', 'Porkokomencanta kurso estis fermita la 12.IV. kaj montris kontentigan rezulton.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2215, 281, NULL, '19', 'En Ĝenevo soveta delegacio proponis projekton de ĝenerals senarmigo.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2216, 281, NULL, '20', '“Dum multaj monatoj ni ĉiam denove komencis la laboron, por fari al ni vojon, grimpi trans malfermitajn fendojn, travindante nin inter la arboj ...”', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2217, 281, NULL, '21', 'Simile kiel ekster nia movado diverspecaj diplomatoj ...', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2218, 281, NULL, '22', 'La kampanaro haltis kaj laŭte alvokis sian edzinon.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2219, 281, NULL, '23', 'La kongresanoj migris tra la arbo kaj vizitis la reĝan kastelon.', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2220, 281, NULL, '24', 'Ĉu la Esperanta instruado devas fariĝi aparta por ĉiu sekso, aŭ ĉu oni devas preferi la kuninstruadon de ambaŭseksaj lernantoj?', NULL, NULL, NULL, '0', '0');
