insert into ekzerceroj (id,ekzerco_id,kodo,numero,demando,respondo,normaligita,bildo,forigita) values (222,37,'ekz09_02',2,'Bela parketo en la centro de la sud-franca urbo Pau ƒµus ricevis la nomon Zamenhof. La urb-estro de Pau, Andr√© Labarr√®re, fariƒùis ministro en majo. Li dufoje subtenis leƒùproponojn, kiuj celas meti Esperanton en egalan situacion kun aliaj lingvoj. Li estas honora membro de la loka Esperanto-societo, kaj pasint-jare li persone malfermis ekspozicion pri Esperanto, kiu restis unu monaton en la urba biblioteko.','Un beau petit parc dans le centre-ville de Pau, dans le sud de la France, vient de recevoir le nom de Zamenhof. Le maire de Pau, Andr√© Labarr√®re, est devenu ministre en mai. Il a soutenu deux fois des propositions de lois, qui ont pour but de mettre l''esp√©ranto en situation √©gale avec les autres langues. Il est membre honoraire de la soci√©t√© locale d''esp√©ranto, et l''ann√©e derni√®re il a personnellement ouvert une exposition sur l''esp√©ranto, qui est rest√©e un mois dans la biblioth√®que municipale.','un beau petit parc dans le centre-ville de pau dans le sud de la france vient de recevoir le nom de zamenhof le maire de pau andr√© labarr√®re est devenu ministre en mai il a soutenu deux fois des propositions de lois qui ont pour but de mettre l''esp√©ranto en situation √©gale avec les autres langues il est membre honoraire de la soci√©t√© locale d''esp√©ranto et l''ann√©e derni√®re il a personnellement ouvert une exposition sur l''esp√©ranto qui est rest√©e un mois dans la biblioth√®que municipale','',0);
