insert into ekzerceroj (id,ekzerco_id,kodo,numero,demando,respondo,normaligita,bildo,forigita) values (421,72,'ekz10_09',9,'Li povas rakont[*] al ili[*], ki[*] estas la knabino: ≈ùi estas Gerda, nova instruist[*] pri kriptaƒµoscienco ([*]novaj sekretaj lingvoj). Ronga, la profesoro pri lingvistiko, volis, ke ≈ùi ven[*] al la universitato [*] instru[*] kaj labor[*] kun li.','Li povas rakont<b>i</b> al <b>ili</b>, kiu estas la knabino: ≈ùi estas Gerda, nova instruist<b>ino</b> pri kriptaƒµoscienco (<b>mal</b>novaj sekretaj lingvoj). Ronga, la profesoro pri lingvistiko, volis, ke ≈ùi ven<b>u</b> al la universitato por instrui kaj labori kun li.','li povas rakonti al ili kiu estas la knabino sxi estas gerda nova instruistino pri kriptajxoscienco malnovaj sekretaj lingvoj ronga la profesoro pri lingvistiko volis ke sxi venu al la universitato por instrui kaj labori kun li','',0);
