insert into ekzerceroj (id,ekzerco_id,kodo,numero,demando,respondo,normaligita,bildo,forigita) values (423,72,'ekz10_11',11,'Tiu flegistino, dika, grasa kaj parolema, nur post long[*] diskut[*] kun Tom, konsciiƒùas [*] tio, ke la afero urƒùas. Se ≈ùi est[*] pli zorgema, ≈ùi [*] rapidus kaj eble ne okazus jeno:','Tiu flegistino, dika, grasa kaj parolema, nur post long<b>a</b> diskut<b>ado</b> kun Tom, konsciiƒùas <b>pri</b> tio, ke la afero urƒùas. Se ≈ùi est<b>us</b> pli zorgema, ≈ùi <b>pli</b> rapidus kaj eble ne okazus jeno:','tiu flegistino dika grasa kaj parolema nur post longa diskutado|diskuto kun tom konsciigxas pri tio ke la afero urgxas se sxi estus pli zorgema sxi pli rapidus kaj eble ne okazus jeno','',0);
