insert into ekzerceroj (id,ekzerco_id,kodo,numero,demando,respondo,normaligita,bildo,forigita) values (436,74,'ekz11_10',10,'Post longa promenado en la urbo ni volis eksidi en trinkejo.<br/>Tuj post kiam ni sid___is ƒâe tablo, iu ulo turnis sin. Li turn___is al ni kaj ekrigardis nin.<br/>Ni eksentis nin malbonaj. Lia rigardado malbonsent___is nin. Ni devis agi.<br/>Ni ne plu restis sidaj, ni restar___is, tiel dev___ante lin forturni sin.<br/>Li komprenis, ke li ƒùenis nin ade rigardante al ni.<br/>Ni kompren___is al li, ke rigardado povas esti maldiskreta.','Post longa promenado en la urbo ni volis eksidi en trinkejo.<br/>Tuj post kiam ni sid<b>iƒù</b>is ƒâe tablo, iu ulo turnis sin. Li turn<b>iƒù</b>is al ni kaj ekrigardis nin.<br/>Ni eksentis nin malbonaj. Lia rigardado malbonsent<b>ig</b>is nin. Ni devis agi.<br/>Ni ne plu restis sidaj, ni restar<b>iƒù</b>is, tiel dev<b>ig</b>ante lin forturni sin.<br/>Li komprenis, ke li ƒùenis nin ade rigardante al ni.<br/>Ni kompren<b>ig</b>is al li, ke rigardado povas esti maldiskreta.','post longa promenado en la urbo ni volis eksidi en trinkejo<br/>tuj post kiam ni sidigxis cxe tablo iu ulo turnis sin li turnigxis al ni kaj ekrigardis nin<br/>ni eksentis nin malbonaj lia rigardado malbonsentigis nin ni devis agi<br/>ni ne plu restis sidaj ni restarigxis tiel devigante lin forturni sin<br/>li komprenis ke li gxenis nin ade rigardante al ni<br/>ni komprenigis al li ke rigardado povas esti maldiskreta','',0);
