INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1806, 221, NULL, '1', '‚Äú... iu reƒùfilino estas ≈ùtelita de ciganoj, ≈ùi suferas kaj mortas ...‚Äù Kial ƒâi tie estas uzita la nuntempo (-as)? La unua parto de la propozicio estas pasiva, reskribu ƒùin aktive. Donu sinonimon de reƒùfilino.', NULL, NULL, NULL, '0', '0');
