INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1808, 221, NULL, '3', '‚ÄúKiel eble plej rapide.‚Äù Skribu propoziciojn, uzante kiel eble plej kun jenaj vortoj : EVIDENTA; IMITINDA; NEGLEKTE; ORIGINALA; ARTIFIKE.', NULL, NULL, NULL, '0', '0');
