INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (1810, 221, NULL, '5', '‚ÄúMi estas bone, kiel vi fartas?‚Äù Jen ekzemplo de idiotisma  Esperanto. Gramatike, la unua frazo devus legiƒùi: ‚ÄúAl mi estas bone‚Äù (kial?), sed ofta kaj kutima uzado de la plena frazo ka≈≠zas, ke la frazo de la a≈≠toro restas tute komprenebla kaj natura. Kial li ne povis skribi ‚ÄúMi estas bona‚Äù?', NULL, NULL, NULL, '0', '0');
