INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2006, 249, NULL, '14', 'ODISEO: ‚Äú... estis virsafoj bonnutritaj kaj denslanaj, belaj kaj grandaj, kun violkolora lano; senbrue mi tiujn kunligis per flekseblaj branƒâetoj, sur kiuj kutimis dormi la Kiklopo, la monstrulo, inklina al maljustaƒµoj, kaj ƒâiufoje mi prenis tri vir≈ùafojn kune; ƒâiu meza portis viron, kaj amba≈≠ aliaj iris amba≈≠flanke por ≈ùirmprotekti la kunulojn. Tiel do ƒâiufoje tri vir≈ùafoj portis unu viron; sed mi ‚Äî ƒâar estis unu vir≈ùafo multsupere plej granda inter ƒâiuj ≈ùafoj ‚Äî ƒâirka≈≠prenis ties dorson kaj sub lia harkovrita ventro kurbigante min, mi tiel restis pendanta; kaj envolvinte la manojn en la densegan lanon mi tenadis min konstante al ƒùi kun persistema animo... Ilia mastro, turmentata de teruraj doloroj, palpadis la dorsojn de ƒâiuj vir≈ùafoj rekte starantaj; sed tion ne rimarkis la stultulo, ke miaj kunuloj estis ligitaj sub la brustoj de la lankovritaj vir≈ùafoj. Laste el ƒâiuj iris eksteren mia vir≈ùafo, ≈ùarƒùite per sia lano kaj per mi kun miaj inteligentaj pensoj... Kiam ni estis venintaj je malgranda distanco de la kaverno kaj la barilo, tiam unue mi malligis min mem de sub la vir≈ùafo kaj tiam mi liberigis miajn kunulojn.‚Äù (‚ÄúOdusseias de Homeros‚Äù, tradukita de W. J. T. Manders.)<br/>ƒàu vi konas, a≈≠ povas eltrovi, aliajn aventurojn de Odiseo?', NULL, NULL, NULL, '0', '0');
INSERT INTO `ekzerceroj` (`id`, `ekzerco_id`, `kodo`, `numero`, `demando`, `respondo`, `normaligita`, `bildo`, `forigita`, `korektebla`) VALUES (2007, 249, NULL, '15', '‚ÄúTra tiuj jaroj oni povis observi la progresantan fronton de la balzaminaj trupoj, ƒùis fine ili nun okupadis la tutan hortikulturejon.‚Äù Kiel nomiƒùas tiu figuro? Klarigu ƒùin.', NULL, NULL, NULL, '0', '0');
